import React, { useState, useEffect } from 'react';
import './ChatPage.css';
import ChatMessage from '../ChatMessage/ChatMessage';
import UploadButton from '../UploadButton/UploadButton'; // Assuming you have this component

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [uploadStatus, setUploadStatus] = useState(null); 

    const apiUrl = 'http://127.0.0.1:5000';

    useEffect(() => {
        const fetchChatHistory = async () => {
            try {
                // Fetching history using Authorization header
                const response = await fetch(`${apiUrl}/chat_history`, {
                    headers: {
                        'Authorization': sessionToken
                    }
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch chat history.');
                }
                const data = await response.json();
                setMessages(data.history || []);
            } catch (err) {
                console.error("Failed to fetch chat history:", err);
            }
        };

        fetchChatHistory();
        // Removed interval for simplicity; fetch on page load/action
    }, [sessionToken]);

    const handleSendMessage = async (userQuery) => {
        if (!userQuery.trim() || isLoading) return;

        setIsLoading(true);
        const newMessage = {
            sender: 'user',
            text: userQuery,
            timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, newMessage]);
        setInput('');

        try {
            const response = await fetch(`${apiUrl}/chat`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    // FIX: Include the Authorization header for protected routes
                    'Authorization': sessionToken, 
                },
                body: JSON.stringify({ query: userQuery }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Failed to get response from bot.');
            }

            const botMessage = {
                sender: 'bot',
                text: data.answer,
                timestamp: new Date().toISOString() 
            };
            
            setMessages(prev => [...prev, botMessage]);

        } catch (err) {
            console.error("Chat error:", err);
            setMessages(prev => [...prev, { sender: 'bot', text: `Error: ${err.message}`, timestamp: new Date().toISOString() }]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleFileUpload = async (file) => {
        if (!file || isLoading) return;

        setIsLoading(true);
        setUploadStatus(`Uploading ${file.name}...`);

        const formData = new FormData();
        formData.append('file', file);
        
        try {
            const response = await fetch(`${apiUrl}/upload`, {
                method: 'POST',
                headers: {
                    // *** CRITICAL FIX: Include the Authorization header for protected routes ***
                    'Authorization': sessionToken, 
                },
                body: formData,
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || data.message || 'File upload failed.');
            }
            
            setUploadStatus('File uploaded successfully. Chatting is now available.');
            // Add bot's confirmation message to the chat history
            setMessages(prev => [...prev, { 
                sender: 'bot', 
                text: data.message || 'File processed successfully.', 
                timestamp: new Date().toISOString() 
            }]);

        } catch (err) {
            console.error("Upload error:", err);
            setUploadStatus(`Error: ${err.message}`);
        } finally {
            setIsLoading(false);
            setTimeout(() => setUploadStatus(null), 5000);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-card">
                <header className="chat-header">
                    <h1 className="text-xl font-bold">Timesheet Bot - User: {user}</h1>
                    <button onClick={onSignOut} className="sign-out-button">Sign Out</button>
                </header>
                
                <div className="chat-messages">
                    {messages.map((msg, index) => (
                        <ChatMessage key={index} sender={msg.sender} text={msg.text} />
                    ))}
                    {isLoading && <ChatMessage sender="bot" text="Thinking..." />} 
                </div>

                <div className="flex p-4 border-t border-slate-700 bg-slate-800 items-center justify-between">
                    <UploadButton onFileUpload={handleFileUpload} />
                    {uploadStatus && <span className="text-sm text-yellow-400">{uploadStatus}</span>}

                    <form onSubmit={(e) => {
                        e.preventDefault();
                        handleSendMessage(input);
                    }} className="flex-1 flex items-center space-x-2 ml-4">
                        <input
                            type="text"
                            placeholder="Ask a question about the timesheet..."
                            className="flex-1 p-3 rounded-lg bg-slate-500 text-white border-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            disabled={isLoading}
                        />
                        <button type="submit" className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors duration-300 font-bold" disabled={isLoading || !input.trim()}>
                            Send
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
