import React from 'react';

const ChatInput = ({ onChatSubmit, isLoading, input, setInput }) => {
    // The component now relies on props for state management

    const handleSubmit = (e) => {
        e.preventDefault();
        const message = input.trim();
        if (message) {
            // Call the handler in ChatPage, which manages the API call and state.
            onChatSubmit(e); 
        }
    };
    
    return (
        // The form takes up the flexible space (flex-1) in the input bar
        <form onSubmit={handleSubmit} className="flex-1 chat-input-form-container">
            <input
                type="text"
                placeholder="Ask a question about the timesheet..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                className="chat-input-field"
                disabled={isLoading}
            />
            <button 
                type="submit" 
                className="chat-send-button" 
                disabled={isLoading || !input.trim()}
                title="Send Message"
            >
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
                </svg>
            </button>
        </form>
    );
};

export default ChatInput;
