import React, { useState, useEffect } from 'react';
import { initializeApp, getApps } from 'firebase/app';
import { getAuth, onAuthStateChanged, signInWithCustomToken } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';
import Login from './components/Login/Login'; // Corrected import path
import ChatPage from './components/ChatPage/ChatPage'; // Corrected import path

// Replace with your actual Firebase config
const firebaseConfig = {
  // Your Firebase config here
};

const initialAuthToken = null; // Replace with your auth token if needed

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if Firebase has already been initialized
    if (getApps().length === 0) {
      const app = initializeApp(firebaseConfig);
    }
    const auth = getAuth();
    const db = getFirestore();

    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });

    if (initialAuthToken) {
      signInWithCustomToken(auth, initialAuthToken).catch(console.error);
    }

    return () => unsubscribe();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
        Loading...
      </div>
    );
  }

  if (!user) {
    return <Login />;
  }

  return <ChatPage user={user} />;
};

export default App;
