import React from 'react';

const UploadButton = ({ onFileUpload }) => {
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        onFileUpload(file);
        e.target.value = null; // Reset the input 
    };

    return (
        <div>
            {/* Styled as a circular button */}
            <label 
                htmlFor="file-upload" 
                className="cursor-pointer bg-gray-600 hover:bg-gray-500 transition-colors duration-300 p-3 rounded-full font-bold text-white flex items-center justify-center shadow-md"
                title="Upload Timesheet (CSV)"
            >
                {/* SVG for Upload Icon */}
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/>
                </svg>
            </label>
            <input
                type="file"
                id="file-upload"
                className="hidden"
                accept=".csv" // Accept only CSV files
                onChange={handleFileChange}
            />
        </div>
    );
};

export default UploadButton;
