import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import {
    getAuth,
    signInWithCustomToken,
    onAuthStateChanged,
    signInAnonymously,
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword
} from 'firebase/auth';
import {
    getFirestore,
    doc,
    setDoc,
    collection,
    query,
    orderBy,
    onSnapshot
} from 'firebase/firestore';
import ChatPage from './components/ChatPage/ChatPage.jsx';
import AuthPage from './components/AuthPage/AuthPage.jsx';

const App = () => {
    // Define appId and initialAuthToken at the top level
    const appId = typeof window.__app_id !== 'undefined' ? window.__app_id : 'default-app-id';
    const initialAuthToken = typeof window.__initial_auth_token !== 'undefined' ? window.__initial_auth_token : null;
    
    const [user, setUser] = useState(null);
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null); // Set up state for auth instance
    const [userId, setUserId] = useState(null);
    const [isLoadingAuth, setIsLoadingAuth] = useState(true);
    const [authError, setAuthError] = useState('');

    useEffect(() => {
        const firebaseConfig = typeof window.__firebase_config !== 'undefined' ? JSON.parse(window.__firebase_config) : {};

        if (firebaseConfig && Object.keys(firebaseConfig).length > 0) {
            try {
                const app = initializeApp(firebaseConfig);
                const authInstance = getAuth(app);
                const dbInstance = getFirestore(app);
                setDb(dbInstance);
                setAuth(authInstance);

                const unsubscribe = onAuthStateChanged(authInstance, async (authUser) => {
                    if (authUser) {
                        setUser(authUser);
                        setUserId(authUser.uid);
                    } else {
                        setUser(null);
                        setUserId(null);
                        setMessages([]);
                    }
                    setIsLoadingAuth(false);
                });

                if (initialAuthToken) {
                    signInWithCustomToken(authInstance, initialAuthToken).catch((error) => {
                        console.error("Error signing in with custom token:", error);
                        signInAnonymously(authInstance).catch(anonError => {
                            console.error("Error signing in anonymously:", anonError);
                        });
                    });
                } else {
                    signInAnonymously(authInstance).catch(error => {
                        console.error("Error signing in anonymously:", error);
                    });
                }

                return () => unsubscribe();
            } catch (error) {
                console.error("Firebase initialization failed:", error);
                setIsLoadingAuth(false);
            }
        } else {
            setIsLoadingAuth(false);
        }
    }, [initialAuthToken]);

    useEffect(() => {
        if (db && userId) {
            const chatHistoryQuery = query(
                collection(db, `/artifacts/${appId}/users/${userId}/chat_history`),
                orderBy('timestamp')
            );

            const unsubscribe = onSnapshot(chatHistoryQuery, (snapshot) => {
                const history = snapshot.docs.map(doc => doc.data()).sort((a, b) => a.timestamp - b.timestamp);
                setMessages(history);
            }, (error) => {
                console.error("Error fetching real-time chat history:", error);
            });

            return () => unsubscribe();
        }
    }, [db, userId, appId]);

    const handleAuthenticate = async (email, password, isLogin) => {
        if (!auth) {
            setAuthError('Authentication service not available.');
            return;
        }

        setLoading(true);
        setAuthError('');
        try {
            if (isLogin) {
                await signInWithEmailAndPassword(auth, email, password);
            } else {
                await createUserWithEmailAndPassword(auth, email, password);
            }
        } catch (err) {
            setAuthError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleSendMessage = async (query) => {
        if (!query || !db || !userId) return;

        const newUserMessage = { sender: 'user', text: query, timestamp: Date.now() };
        setLoading(true);

        try {
            await setDoc(doc(collection(db, `/artifacts/${appId}/users/${userId}/chat_history`)), newUserMessage);
            
            const API_URL = "http://127.0.0.1:5000/review";
            const response = await fetch(API_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ query }),
            });

            const data = await response.json();
            const newBotMessage = { sender: 'bot', text: data.answer, timestamp: Date.now() };
            
            await setDoc(doc(collection(db, `/artifacts/${appId}/users/${userId}/chat_history`)), newBotMessage);
        } catch (error) {
            console.error("Error fetching bot response or saving to DB:", error);
            const errorMessage = { sender: 'bot', text: "Sorry, I couldn't get a response. Please try again.", timestamp: Date.now() };
            await setDoc(doc(collection(db, `/artifacts/${appId}/users/${userId}/chat_history`)), errorMessage);
        } finally {
            setLoading(false);
        }
    };

    const handleUpdate = async () => {
        console.log("Update button clicked. This would typically trigger a file upload or data refresh.");
        const botUpdateMessage = { sender: 'bot', text: "Update successful! File uploaded and data stored successfully.", timestamp: Date.now() };
        if (db && userId) {
             await setDoc(doc(collection(db, `/artifacts/${appId}/users/${userId}/chat_history`)), botUpdateMessage);
        }
    };

    if (isLoadingAuth) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white text-lg">
                Loading...
            </div>
        );
    }

    if (!user) {
        return <AuthPage 
            handleAuthenticate={handleAuthenticate}
            loading={loading}
            error={authError}
        />;
    }

    return (
        <ChatPage
            messages={messages}
            handleSendMessage={handleSendMessage}
            loading={loading}
            handleUpdate={handleUpdate}
            userId={userId}
        />
    );
};

export default App;
