import React, { useState, useEffect } from 'react';
import { createRoot } from 'react-dom/client';

// All components are self-contained in this single file.
const App = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [authError, setAuthError] = useState('');
  const [sessionToken, setSessionToken] = useState(null);
  const [messages, setMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Polling to fetch chat history every 2 seconds
  useEffect(() => {
    let pollingInterval;
    if (sessionToken) {
      const fetchHistory = async () => {
        try {
          const response = await fetch(`/chat_history?session_token=${sessionToken}`);
          if (response.ok) {
            const data = await response.json();
            setMessages(data.history);
          }
        } catch (error) {
          console.error("Failed to fetch chat history:", error);
        }
      };
      fetchHistory(); // Initial fetch
      pollingInterval = setInterval(fetchHistory, 2000);
    }
    return () => clearInterval(pollingInterval);
  }, [sessionToken]);

  const handleAuthSubmit = async (e) => {
    e.preventDefault();
    setAuthError('');
    const endpoint = isSignUp ? '/signup' : '/login';
    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.error);
      }
      if (data.session_token) {
        setSessionToken(data.session_token);
      }
    } catch (err) {
      setAuthError(err.message);
    }
  };

  const handleSignOut = () => {
    setSessionToken(null);
    setUsername('');
    setPassword('');
    setMessages([]);
  };

  const handleChatSubmit = async (e) => {
    e.preventDefault();
    const message = chatInput.trim();
    if (!message) return;
    setChatInput('');
    setIsLoading(true);

    const userMessage = { sender: 'user', text: message, timestamp: Date.now() };
    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ session_token: sessionToken, message: message }),
      });
      if (!response.ok) throw new Error('Failed to send message.');
    } catch (err) {
      console.error(err);
      setMessages(prev => [...prev, { sender: 'bot', text: 'An error occurred. Please try again.', timestamp: Date.now() }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setIsLoading(true);
    setMessages(prev => [...prev, { sender: 'user', text: `Uploading file: ${file.name}...`, timestamp: Date.now() }]);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('session_token', sessionToken);

    try {
      const response = await fetch('/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error);
    } catch (err) {
      console.error("Upload failed:", err);
      setMessages(prev => [...prev, { sender: 'bot', text: `Upload failed: ${err.message}`, timestamp: Date.now() }]);
    } finally {
      e.target.value = '';
      setIsLoading(false);
    }
  };

  const ChatBubble = ({ sender, text }) => (
    <div className={`p-3 rounded-xl max-w-[80%] break-words shadow-md ${sender === 'user' ? 'bg-blue-600 text-white self-end' : 'bg-slate-600 text-slate-200 self-start'}`}>
      {text}
    </div>
  );

  return (
    <div className="bg-slate-800 text-white min-h-screen flex items-center justify-center p-4">
      {!sessionToken ? (
        <div className="bg-slate-700 p-8 rounded-xl shadow-lg w-full max-w-sm">
          <h2 className="text-2xl font-bold mb-6 text-center">{isSignUp ? 'Sign Up' : 'Log In'}</h2>
          <form onSubmit={handleAuthSubmit} className="space-y-4">
            <input
              type="text"
              placeholder="Username"
              required
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full p-3 rounded-lg bg-slate-600 border-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
            />
            <input
              type="password"
              placeholder="Password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 rounded-lg bg-slate-600 border-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
            />
            {authError && <p className="text-red-400 text-sm text-center">{authError}</p>}
            <button type="submit" className="w-full p-3 rounded-lg bg-blue-600 hover:bg-blue-700 transition-colors duration-300 font-bold">
              {isSignUp ? 'Sign Up' : 'Log In'}
            </button>
          </form>
          <button onClick={() => setIsSignUp(!isSignUp)} className="w-full text-sm text-blue-400 mt-4 underline hover:text-blue-300 transition-colors duration-300">
            {isSignUp ? 'Already have an account? Log In' : 'Need an account? Sign Up'}
          </button>
        </div>
      ) : (
        <div className="bg-slate-700 rounded-xl shadow-lg w-full max-w-4xl h-[90vh] flex flex-col">
          <header className="bg-slate-600 p-4 rounded-t-xl flex justify-between items-center shadow">
            <h2 className="text-xl font-bold">Timesheet Reviewer Bot</h2>
            <button onClick={handleSignOut} className="bg-red-600 text-white px-4 py-2 rounded-lg font-semibold hover:bg-red-700 transition-colors duration-300">
              Sign Out
            </button>
          </header>

          <div className="flex-1 overflow-y-auto p-4 space-y-4 flex flex-col">
            {messages.map((msg, index) => (
              <ChatBubble key={index} sender={msg.sender} text={msg.text} />
            ))}
          </div>

          <form onSubmit={handleChatSubmit} className="bg-slate-600 p-4 rounded-b-xl flex items-center space-x-2">
            <label htmlFor="file-upload" className="cursor-pointer bg-slate-500 hover:bg-slate-400 transition-colors duration-300 p-3 rounded-lg">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12" />
              </svg>
            </label>
            <input
              type="file"
              id="file-upload"
              className="hidden"
              onChange={handleFileUpload}
            />
            
            <input
              type="text"
              placeholder="Ask a question about the timesheet..."
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              className="flex-1 p-3 rounded-lg bg-slate-500 border-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
              disabled={isLoading}
            />
            <button type="submit" className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors duration-300 font-bold" disabled={isLoading}>
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
              </svg>
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

const container = document.getElementById('root');
const root = createRoot(container);
root.render(<App />);

export default App;
