import React, { useState, useEffect, useRef } from 'react';
import ChatMessage from '../ChatMessage/ChatMessage.jsx';
import UploadButton from '../UploadButton/UploadButton.jsx';

const API_URL = "http://127.0.0.1:5000/review";

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [inputValue, setInputValue] = useState('');
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = async (query) => {
        if (!query) return;

        const newUserMessage = { sender: 'user', text: query, timestamp: Date.now() };
        setMessages(prevMessages => [...prevMessages, newUserMessage]);
        setLoading(true);

        try {
            const response = await fetch(API_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            });

            const data = await response.json();
            const newBotMessage = { sender: 'bot', text: data.answer, timestamp: Date.now() };
            setMessages(prevMessages => [...prevMessages, newBotMessage]);
        } catch (error) {
            console.error("Error fetching bot response:", error);
            const errorMessage = { sender: 'bot', text: "Sorry, I couldn't get a response. Please try again.", timestamp: Date.now() };
            setMessages(prevMessages => [...prevMessages, errorMessage]);
        } finally {
            setLoading(false);
        }
    };

    const handleUpdate = () => {
        const updateMessage = { sender: 'bot', text: "Update successful! File uploaded and data stored successfully.", timestamp: Date.now() };
        setMessages(prevMessages => [...prevMessages, updateMessage]);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (inputValue.trim()) {
            handleSendMessage(inputValue);
            setInputValue('');
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
            <div className="bg-gray-800 rounded-xl shadow-2xl overflow-hidden w-full max-w-lg flex flex-col h-[80vh]">
                <div className="p-4 bg-gray-700 border-b border-gray-600 flex justify-between items-center">
                    <h1 className="text-xl font-bold">Timesheet Reviewer Bot</h1>
                    <span className="flex items-center text-sm text-green-400">
                        <span className="w-2.5 h-2.5 bg-green-400 rounded-full mr-2 animate-pulse"></span>
                        Status: Online
                    </span>
                </div>
                <div className="flex-1 overflow-y-auto p-4 space-y-4">
                    {messages.map((msg, index) => (
                        <ChatMessage key={index} message={msg} />
                    ))}
                    {loading && (
                        <div className="flex justify-start">
                            <div className="bg-gray-600 text-white p-3 rounded-r-lg rounded-bl-lg max-w-xs animate-pulse">
                                ...
                            </div>
                        </div>
                    )}
                    <div ref={messagesEndRef} />
                </div>
                <div className="p-4 bg-gray-700 border-t border-gray-600 flex items-center space-x-2">
                    <UploadButton handleUpdate={handleUpdate} />
                    <form onSubmit={handleSubmit} className="flex-1 flex space-x-2">
                        <input
                            type="text"
                            value={inputValue}
                            onChange={(e) => setInputValue(e.target.value)}
                            className="flex-1 p-3 rounded-lg bg-gray-600 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                            placeholder="Ask a question about the timesheet..."
                            disabled={loading}
                        />
                        <button
                            type="submit"
                            className="px-4 py-3 bg-indigo-600 text-white rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200 disabled:bg-gray-600 disabled:cursor-not-allowed"
                            disabled={loading}
                        >
                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-5 h-5">
                                <path d="M3.478 2.405a.75.75 0 0 0-.926.94l2.432 7.917H16.5a.75.75 0 0 1 0 1.5H4.984l-2.432 7.917a.75.75 0 0 0 .926.94 60.519 60.519 0 0 0 18.445-8.986.75.75 0 0 0 0-1.218A60.517 60.517 0 0 0 3.478 2.405Z" />
                            </svg>
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
