// ChatHistory.jsx
import React from 'react';

const ChatHistory = ({ history, onSelectChat }) => {
    return (
        // w-1/4 sets the width of the sidebar (approx 25%)
        <div className="w-1/4 p-4 bg-gray-700 overflow-y-auto">
            <h3 className="text-lg font-bold mb-4 border-b border-gray-600 pb-2 text-white">
                Chat History
            </h3>
            <ul className="space-y-2">
                {history.map((chat) => (
                    <li
                        key={chat.id}
                        className="p-3 rounded-lg bg-gray-600 hover:bg-gray-500 cursor-pointer transition-colors duration-200"
                        onClick={() => onSelectChat(chat.id)}
                    >
                        <p className="text-sm font-medium">{chat.title}</p>
                        <p className="text-xs text-gray-400">{chat.lastUpdated}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ChatHistory;
