import React from 'react';
import ChatInput from './ChatInput';
import ChatMessage from './ChatMessage';

const ChatPage = ({ messages, handleSendMessage, loading }) => {
    return (
        <div className="flex flex-col h-full bg-gray-900 text-white p-6 rounded-2xl shadow-2xl">
            <header className="chat-header text-center py-4 border-b border-gray-700">
                <h2 className="text-3xl font-extrabold text-indigo-400">Timesheet Reviewer Bot</h2>
                <span className="status-online inline-flex items-center text-sm font-medium text-green-400 mt-1">
                    <span className="status-dot w-2 h-2 mr-2 bg-green-500 rounded-full animate-pulse"></span>
                    Status: Online
                </span>
            </header>
            <div className="flex-grow overflow-y-auto my-4 p-4 rounded-xl bg-gray-800 space-y-4">
                {messages.map((msg, index) => (
                    <ChatMessage key={index} sender={msg.sender} text={msg.text} />
                ))}
                {loading && (
                    <div className="flex justify-start items-center">
                        <div className="bg-gray-700 text-gray-300 p-3 rounded-xl rounded-bl-none shadow-md max-w-xs animate-pulse">
                            <span className="block">Bot is typing...</span>
                        </div>
                    </div>
                )}
            </div>
            <ChatInput onSend={handleSendMessage} disabled={loading} />
        </div>
    );
};

export default ChatPage;
