import React, { useState, useEffect, useRef } from 'react';
import ChatInput from '../ChatInput/ChatInput.jsx';
import ChatMessage from '../ChatMessage/ChatMessage.jsx';

const API_URL = "http://127.0.0.1:5000/review";

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = async (query) => {
        if (!query) return;

        const newUserMessage = { sender: 'user', text: query };
        setMessages(prevMessages => [...prevMessages, newUserMessage]);
        setLoading(true);

        try {
            const response = await fetch(API_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            });

            const data = await response.json();
            const newBotMessage = { sender: 'bot', text: data.answer };
            setMessages(prevMessages => [...prevMessages, newBotMessage]);
        } catch (error) {
            console.error("Error fetching bot response:", error);
            const errorMessage = { sender: 'bot', text: "Sorry, I couldn't get a response. Please try again." };
            setMessages(prevMessages => [...prevMessages, errorMessage]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
            <div className="bg-gray-800 rounded-xl shadow-2xl overflow-hidden w-full max-w-lg flex flex-col h-[80vh]">
                <div className="p-4 bg-gray-700 border-b border-gray-600 flex justify-between items-center">
                    <h1 className="text-xl font-bold">Timesheet Reviewer Bot</h1>
                    <span className="flex items-center text-sm text-green-400">
                        <span className="w-2.5 h-2.5 bg-green-400 rounded-full mr-2 animate-pulse"></span>
                        Status: Online
                    </span>
                </div>
                <div className="flex-1 overflow-y-auto p-4 space-y-4">
                    {messages.map((msg, index) => (
                        <ChatMessage key={index} message={msg} />
                    ))}
                    {loading && (
                        <div className="flex justify-start">
                            <div className="bg-gray-600 text-white p-3 rounded-r-lg rounded-bl-lg max-w-xs animate-pulse">
                                ...
                            </div>
                        </div>
                    )}
                    <div ref={messagesEndRef} />
                </div>
                <div className="p-4 bg-gray-700 border-t border-gray-600 flex items-center space-x-2">
                    <ChatInput onSend={handleSendMessage} disabled={loading} />
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
