import React from 'react';

const ChatMessage = ({ message }) => {
    const isUser = message.sender === 'user';
    const messageClass = isUser ? 'bg-indigo-600 text-white self-end rounded-bl-lg' : 'bg-gray-600 text-white self-start rounded-br-lg';

    return (
        <div className={`p-3 rounded-lg max-w-xs md:max-w-md ${messageClass}`}>
            {message.text}
        </div>
    );
};

export default ChatMessage;
