import React, { useState, useEffect, useRef } from 'react';
import './ChatPage.css';
import ChatInput from '../ChatInput/ChatInput'; 
import UploadButton from '../UploadButton/UploadButton'; 
import ChatMessage from '../ChatMessage/ChatMessage'; // Using your ChatMessage component

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    // 1. STATE INITIALIZATION
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [uploadStatus, setUploadStatus] = useState('');
    const messagesEndRef = useRef(null);

    // Function to scroll to the bottom of the chat
    const scrollToBottom = () => {
        // Ensure smooth scrolling to the latest message
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };
    
    // 2. CHAT HISTORY POLLING AND SCROLL EFFECT
    useEffect(() => {
        const fetchChatHistory = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:5000/chat_history?session_token=${sessionToken}`);
                if (!response.ok) {
                    console.error('Failed to fetch chat history. Status:', response.status);
                    return;
                }
                const data = await response.json();
                
                // FIX FOR 'map is undefined': Ensure data.history is a safe array.
                // The backend returns {"history": [...]}, so we ensure the value is an array.
                setMessages(Array.isArray(data.history) ? data.history : []);
                
            } catch (err) {
                console.error("Failed to fetch chat history:", err);
            }
        };

        fetchChatHistory();
        // Poll for new messages every 3 seconds
        const intervalId = setInterval(fetchChatHistory, 3000);

        return () => clearInterval(intervalId);
    }, [sessionToken]);

    // Scroll to bottom whenever messages update
    useEffect(() => {
        scrollToBottom();
    }, [messages]);


    // 3. FILE UPLOAD HANDLER
    const handleFileUpload = async (file) => {
        if (!file) return;

        setIsLoading(true);
        setUploadStatus(`Uploading ${file.name}...`);
        
        // Optimistically add a user message indicating upload started
        setMessages(prev => [...prev, {
            sender: 'user',
            text: `Uploading timesheet file: ${file.name}...`,
            timestamp: new Date().toISOString()
        }]);

        const formData = new FormData();
        formData.append('file', file);
        formData.append('session_token', sessionToken);
        formData.append('timestamp', new Date().toISOString());

        try {
            const response = await fetch('http://127.0.0.1:5000/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'File upload failed.');
            }

            // Success message will be fetched by the polling mechanism
            setUploadStatus(`Upload of ${file.name} complete!`);

        } catch (err) {
            console.error("Upload Error:", err);
            setUploadStatus(`Error: ${err.message}`);
            // Display an error message to the user
            setMessages(prev => [...prev.slice(0, -1), {
                sender: 'bot',
                text: `Sorry, the upload failed: ${err.message}`,
                timestamp: new Date().toISOString()
            }]);
        } finally {
            setIsLoading(false);
            setTimeout(() => setUploadStatus(''), 5000); // Clear status after 5 seconds
        }
    };


    // 4. SEND MESSAGE HANDLER
    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!input.trim() || isLoading) return;

        const userMessage = {
            sender: 'user',
            text: input,
            timestamp: new Date().toISOString()
        };

        // Optimistically update the UI with the user's message
        setMessages(prev => [...prev, userMessage]);
        setInput('');
        setIsLoading(true);
        
        try {
            const response = await fetch('http://127.0.0.1:5000/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query: userMessage.text, session_token: sessionToken }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'Failed to get bot response.');
            }

            const data = await response.json();
            
            // Add bot's immediate response to avoid waiting for 3-second poll
            if (data.answer) {
                 setMessages(prev => [...prev, {
                    sender: 'bot',
                    text: data.answer,
                    timestamp: new Date().toISOString()
                 }]);
            }
        } catch (err) {
            console.error("Chat Error:", err);
            // Add a bot error message
            setMessages(prev => [...prev, {
                sender: 'bot',
                text: `An error occurred: ${err.message}`,
                timestamp: new Date().toISOString()
            }]);
        } finally {
            setIsLoading(false);
        }
    };

    // 5. RENDER LOGIC
    return (
        <div className="chat-container">
            <div className="chat-card">
                <header className="chat-header">
                    <h1 className="text-xl font-bold">Timesheet Bot ðŸ¤–</h1>
                    <div className="flex items-center space-x-4">
                        <span className="text-sm text-gray-400">Welcome, {user}!</span>
                        <button onClick={onSignOut} className="sign-out-button">
                            Sign Out
                        </button>
                    </div>
                </header>
                
                {/* Main Content Area: Ensures chat area takes available height using flex-1 */}
                <main className="flex-1 flex flex-col p-4 overflow-y-auto">
                    
                    {/* Render Upload Status */}
                    {uploadStatus && (
                        <div className={`p-2 my-2 text-sm text-center rounded-lg ${uploadStatus.startsWith('Error') ? 'bg-red-500' : 'bg-green-500'}`}>
                            {uploadStatus}
                        </div>
                    )}
                    
                    {/* Chat Messages Container */}
                    <div className="chat-messages flex-1 flex flex-col space-y-3">
                        {/* FIX: Use ChatMessage component and safely map over the array */}
                        {Array.isArray(messages) && messages.map((msg, index) => (
                            <ChatMessage 
                                key={index} 
                                sender={msg.sender} 
                                text={msg.text} 
                            />
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                </main>

                {/* Input Area */}
                <div className="p-4 border-t border-gray-700 flex items-center space-x-3 bg-gray-800">
                    <UploadButton onFileUpload={handleFileUpload} />
                    <ChatInput onChatSubmit={handleSendMessage} isLoading={isLoading} input={input} setInput={setInput} />
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
