import React, { useState, useEffect } from 'react';
import { onAuthStateChanged } from 'firebase/auth';
import { auth, db } from './firebase'; 
import Login from './components/Login/Login';
import ChatPage from './components/ChatPage/ChatPage';

const App = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [loginError, setLoginError] = useState('');

    useEffect(() => {
        // Listen for authentication state changes
        const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
            setUser(currentUser);
            setLoading(false);
        });

        return () => unsubscribe();
    }, []);

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
                Loading...
            </div>
        );
    }

    if (!user) {
        // Pass auth and error state to the Login component
        return <Login auth={auth} setLoginError={setLoginError} loginError={loginError} />;
    }

    // Pass the user and db instance to the ChatPage component
    return <ChatPage user={user} db={db} />;
};

export default App;
