import React, { useState } from 'react';
import { createRoot } from 'react-dom/client';
import ChatPage from './components/ChatPage/ChatPage';
import AuthPage from './components/AuthPage/AuthPage';
import './App.css'; // Assuming you have some basic styling here

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [sessionToken, setSessionToken] = useState(null);

  return (
    <div className="bg-slate-800 min-h-screen flex items-center justify-center p-4">
      {isLoggedIn && sessionToken ? (
        <ChatPage sessionToken={sessionToken} onSignOut={() => setIsLoggedIn(false)} />
      ) : (
        <AuthPage setIsLoggedIn={setIsLoggedIn} setSessionToken={setSessionToken} />
      )}
    </div>
  );
};

const container = document.getElementById('root');
if (container) {
  const root = createRoot(container);
  root.render(<App />);
}

export default App;
