import React from 'react';
import './ChatPage.css'; // Import the main CSS file for styles

const UploadButton = ({ onFileUpload, disabled }) => {
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            onFileUpload(file);
        }
        e.target.value = null; 
    };

    return (
        <div>
            <label 
                htmlFor="file-upload" 
                // Use the new CSS class names
                className={`upload-label ${disabled ? 'disabled' : ''}`} 
            >
                Upload File
            </label>
            <input
                type="file"
                id="file-upload"
                // Use the new CSS class name
                className="hidden" 
                onChange={handleFileChange}
                disabled={disabled}
            />
        </div>
    );
};

export default UploadButton;
