import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './ForgotPassword.css';

const API_BASE_URL = 'http://127.0.0.1:5000';

const ForgotPassword = () => {
    const [step, setStep] = useState(1); // 1: Verify username, 2: Reset password
    const [username, setUsername] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleVerifyUsername = async (e) => {
        e.preventDefault();
        setError('');
        setMessage('');

        if (!username.trim()) {
            setError('Please enter your username.');
            return;
        }

        setIsLoading(true);

        try {
            const response = await fetch(`${API_BASE_URL}/verify_username`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Username verification failed.');
            }

            setMessage("Username verified. Please set a new password.");
            setStep(2);

        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    const handleResetPassword = async (e) => {
        e.preventDefault();
        setError('');
        setMessage('');

        if (!username.trim()) {
            setError('Error: Username state lost. Please restart the process.');
            setStep(1);
            return;
        }

        if (newPassword.length < 6) {
            setError('New password must be at least 6 characters long.');
            return;
        }

        if (newPassword !== confirmPassword) {
            setError('Passwords do not match.');
            return;
        }

        setIsLoading(true);

        try {
            const response = await fetch(`${API_BASE_URL}/reset_password`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ 
                    username, 
                    new_password: newPassword 
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Password reset failed.');
            }

            setMessage(data.message);
            
            // Redirect to login after successful reset
            setTimeout(() => {
                navigate('/login', { 
                    state: { 
                        message: 'Password successfully reset. Please log in with your new password.' 
                    } 
                });
            }, 3000);

        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    const resetProcess = () => {
        setStep(1);
        setUsername('');
        setNewPassword('');
        setConfirmPassword('');
        setError('');
        setMessage('');
    };

    return (
        <div className="forgot-password-container">
            <div className="forgot-password-card">
                <h2 className="forgot-password-title">
                    {step === 1 ? 'Reset Password' : 'Set New Password'}
                </h2>

                {error && <div className="forgot-password-error">{error}</div>}
                {message && <div className="forgot-password-success">{message}</div>}

                {step === 1 ? (
                    <form onSubmit={handleVerifyUsername} className="forgot-password-form">
                        <p className="forgot-password-info">
                            Enter your username to find your account and reset your password.
                        </p>
                        
                        <div className="form-row">
                            <input
                                type="text"
                                placeholder="Enter Username"
                                className="forgot-password-input"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                                autoComplete="username"
                            />
                        </div>

                        <button 
                            type="submit" 
                            className="forgot-password-button"
                            disabled={isLoading}
                        >
                            {isLoading ? 'Verifying...' : 'Verify Username'}
                        </button>
                    </form>
                ) : (
                    <form onSubmit={handleResetPassword} className="forgot-password-form">
                        <p className="forgot-password-info">
                            Set a new password for user: <strong>{username}</strong>
                        </p>
                        
                        <div className="form-row">
                            <input
                                type="password"
                                placeholder="New Password (min 6 characters)"
                                className="forgot-password-input"
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                                required
                                autoComplete="new-password"
                            />
                        </div>

                        <div className="form-row">
                            <input
                                type="password"
                                placeholder="Confirm New Password"
                                className="forgot-password-input"
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                                required
                                autoComplete="new-password"
                            />
                        </div>

                        <button 
                            type="submit" 
                            className="forgot-password-button"
                            disabled={isLoading || !newPassword || newPassword !== confirmPassword}
                        >
                            {isLoading ? 'Resetting...' : 'Reset Password'}
                        </button>

                        <button 
                            type="button" 
                            onClick={resetProcess}
                            className="back-button"
                        >
                            ‚Üê Back to Username Verification
                        </button>
                    </form>
                )}

                <div className="forgot-password-links">
                    <p>
                        Remember your password? <Link to="/login" className="auth-link">Log In</Link>
                    </p>
                    <p>
                        Need an account? <Link to="/signup" className="auth-link">Sign Up</Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default ForgotPassword;
