import React, { useState, useEffect } from 'react';
import './ChatPage.css';

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    useEffect(() => {
        const fetchChatHistory = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:5000/chat_history?session_token=${sessionToken}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch chat history.');
                }
                const data = await response.json();
                setMessages(data.history || []);
            } catch (err) {
                console.error("Failed to fetch chat history:", err);
            }
        };

        fetchChatHistory();
        const intervalId = setInterval(fetchChatHistory, 3000);

        return () => clearInterval(intervalId);
    }, [sessionToken]);

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!input.trim()) return;

        const newMessage = {
            sender: 'user',
            text: input,
            timestamp: new Date().toISOString()
        };

        try {
            const response = await fetch('http://127.0.0.1:5000/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ session_token: sessionToken, message: input, timestamp: newMessage.timestamp }),
            });

            if (!response.ok) {
                throw new Error('Failed to send message.');
            }
            
            setInput('');
        } catch (err) {
            console.error("Failed to send message:", err);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-card">
                <header className="chat-header">
                    <h2 className="chat-title">Timesheet Reviewer Bot</h2>
                    <div className="status-container">
                         <span className="status-dot"></span> Status: Online
                    </div>
                    <button onClick={onSignOut} className="sign-out-button">Sign Out</button>
                </header>
                <div className="chat-messages">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender === 'user' ? 'user-message' : 'bot-message'}`}>
                            <p className="message-text">{msg.text}</p>
                        </div>
                    ))}
                </div>
                <form onSubmit={handleSendMessage} className="chat-input-form">
                    <button type="submit" className="update-button">Update</button>
                    <input
                        type="text"
                        placeholder="Ask a question about the timesheet..."
                        className="chat-input"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                    />
                    <button type="submit" className="send-button">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor" className="send-icon">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M6 12L3.269 3.126A59.768 59.768 0 0121.485 12 59.77 0 013.27 20.876L5.999 12zm0 0h7.5" />
                        </svg>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default ChatPage;
