import React from 'react';

const UploadButton = ({ onFileUpload, disabled }) => {
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            onFileUpload(file);
        }
        e.target.value = null; 
    };

    return (
        <div>
            <label 
                htmlFor="file-upload" 
                className={`cursor-pointer px-4 py-3 rounded-lg font-bold text-white transition-colors duration-300 ${disabled ? 'bg-slate-700 cursor-not-allowed' : 'bg-slate-500 hover:bg-slate-400'}`}
            >
                Upload
            </label>
            <input
                type="file"
                id="file-upload"
                className="hidden"
                onChange={handleFileChange}
                disabled={disabled}
            />
        </div>
    );
};

export default UploadButton;
