import React, { useState, useEffect, useRef } from 'react';
import './ChatPage.css';
import ChatInput from '../ChatInput/ChatInput'; 
import UploadButton from '../UploadButton/UploadButton'; 
import ChatMessage from '../ChatMessage/ChatMessage'; 

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [uploadStatus, setUploadStatus] = useState('');
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };
    
    // Fetch Chat History Interval (includes fix for map error)
    useEffect(() => {
        const fetchChatHistory = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:5000/chat_history?session_token=${sessionToken}`);
                if (!response.ok) {
                    console.error('Failed to fetch chat history. Status:', response.status);
                    return;
                }
                const data = await response.json();
                // FIX: Ensure messages is an array
                setMessages(Array.isArray(data.history) ? data.history : []);
            } catch (err) {
                console.error("Failed to fetch chat history:", err);
            }
        };

        fetchChatHistory();
        const intervalId = setInterval(fetchChatHistory, 3000);
        return () => clearInterval(intervalId);
    }, [sessionToken]);

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleFileUpload = async (file) => {
        if (!file) return;
        setIsLoading(true);
        setUploadStatus(`Uploading ${file.name}...`);
        
        // ... (File upload logic - use fetch) ...

        try {
            // ... (Your existing fetch to /upload) ...
            const formData = new FormData();
            formData.append('file', file);
            formData.append('session_token', sessionToken);
            
            const response = await fetch('http://127.0.0.1:5000/upload', {
                method: 'POST',
                body: formData,
            });
            
            if (!response.ok) {
                 const errorData = await response.json();
                 throw new Error(errorData.error || 'File upload failed.');
            }

            setUploadStatus(`Upload of ${file.name} complete!`);

        } catch (err) {
            setUploadStatus(`Error: ${err.message}`);
        } finally {
            setIsLoading(false);
            setTimeout(() => setUploadStatus(''), 5000);
        }
    };

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!input.trim() || isLoading) return;

        // ... (Send message logic - uses fetch to /chat) ...
        const userMessage = { sender: 'user', text: input, timestamp: new Date().toISOString() };
        setMessages(prev => [...prev, userMessage]);
        setInput('');
        setIsLoading(true);
        
        try {
            const response = await fetch('http://127.0.0.1:5000/chat', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ query: userMessage.text, session_token: sessionToken }),
            });

            if (response.ok) {
                const data = await response.json();
                if (data.answer) {
                     setMessages(prev => [...prev, { sender: 'bot', text: data.answer, timestamp: new Date().toISOString() }]);
                }
            } else {
                throw new Error("Failed to get bot response.");
            }
        } catch (err) {
            setMessages(prev => [...prev, { sender: 'bot', text: `An error occurred: ${err.message}`, timestamp: new Date().toISOString() }]);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-card">
                <header className="chat-header">
                    <h1 className="text-xl font-bold">Timesheet Bot ðŸ¤–</h1>
                    <div className="flex items-center space-x-4">
                        <span className="text-sm text-gray-400">Welcome, {user}!</span>
                        <button onClick={onSignOut} className="sign-out-button">
                            Sign Out
                        </button>
                    </div>
                </header>
                
                {/* Scrollable Content Area */}
                <div className="chat-content-area">
                    
                    {/* Render Upload Status */}
                    {uploadStatus && (
                        <div className={`upload-status-message ${uploadStatus.startsWith('Error') ? 'upload-status-error' : 'upload-status-success'}`}>
                            {uploadStatus}
                        </div>
                    )}
                    
                    {/* Chat Messages Container */}
                    <div className="chat-messages">
                        {Array.isArray(messages) && messages.map((msg, index) => (
                            <ChatMessage 
                                key={index} 
                                sender={msg.sender} 
                                text={msg.text} 
                            />
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                </div>

                {/* Fixed Input Area */}
                <div className="chat-input-area">
                    <UploadButton onFileUpload={handleFileUpload} />
                    <ChatInput 
                        onChatSubmit={handleSendMessage} 
                        isLoading={isLoading} 
                        input={input} 
                        setInput={setInput} 
                    />
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
