import React, { useState, useEffect } from 'react';

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    useEffect(() => {
        const fetchChatHistory = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:5000/chat_history?session_token=${sessionToken}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch chat history.');
                }
                const data = await response.json();
                setMessages(data.history || []);
            } catch (err) {
                console.error("Failed to fetch chat history:", err);
            }
        };

        fetchChatHistory();
        const intervalId = setInterval(fetchChatHistory, 3000);

        return () => clearInterval(intervalId);
    }, [sessionToken]);

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!input.trim()) return;

        const newMessage = {
            sender: 'user',
            text: input,
            timestamp: new Date().toISOString()
        };

        try {
            const response = await fetch('http://127.00.1:5000/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ session_token: sessionToken, message: input, timestamp: newMessage.timestamp }),
            });

            if (!response.ok) {
                throw new Error('Failed to send message.');
            }
            
            setInput('');
        } catch (err) {
            console.error("Failed to send message:", err);
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4 font-sans">
            <div className="flex w-full max-w-5xl h-[90vh] bg-gray-800 rounded-xl shadow-2xl overflow-hidden">
                <div className="flex flex-col flex-1">
                    <header className="flex justify-between items-center p-4 bg-gray-700 text-white shadow-md">
                        <h2 className="text-xl font-bold">Timesheet Reviewer Bot</h2>
                        <div className="flex items-center space-x-4">
                             <span className="text-green-400 text-sm flex items-center">
                                <span className="w-2 h-2 bg-green-400 rounded-full mr-1 animate-pulse"></span> Status: Online
                            </span>
                            <button onClick={onSignOut} className="p-2 rounded-lg bg-red-600 hover:bg-red-700 transition duration-300">Sign Out</button>
                        </div>
                    </header>
                    <div className="flex-1 overflow-y-auto p-4 flex flex-col space-y-2">
                        {messages.map((msg, index) => (
                            <div key={index} className={`my-2 p-3 rounded-xl max-w-sm ${msg.sender === 'user' ? 'self-end bg-blue-600 text-white' : 'self-start bg-gray-700 text-gray-200'}`}>
                                <p className="text-sm">{msg.text}</p>
                            </div>
                        ))}
                    </div>
                    <form onSubmit={handleSendMessage} className="p-4 bg-gray-700 flex space-x-2">
                        <input
                            type="text"
                            placeholder="Ask a question about the timesheet..."
                            className="flex-1 p-3 rounded-lg bg-gray-600 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                        />
                        <button type="submit" className="p-3 rounded-lg bg-indigo-600 text-white hover:bg-indigo-700 transition duration-300">
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor" className="w-6 h-6 rotate-90">
                                <path strokeLinecap="round" strokeLinejoin="round" d="M6 12L3.269 3.126A59.768 59.768 0 0121.485 12 59.77 0 013.27 20.876L5.999 12zm0 0h7.5" />
                            </svg>
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
