import React, { useState, useEffect } from 'react';
import ChatMessage from './ChatMessage/ChatMessage';
import ChatInput from './ChatInput/';
import UploadButton from './UploadButton';

const ChatPage = ({ sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [chats, setChats] = useState({ 'chat-1': [] });
    const [activeChatId, setActiveChatId] = useState('chat-1');
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        let pollingInterval;
        if (sessionToken) {
            const fetchHistory = async () => {
                try {
                    const response = await fetch(`/chat_history?session_token=${sessionToken}`);
                    if (response.ok) {
                        const data = await response.json();
                        const serverHistory = data.history || [];
                        setMessages(serverHistory);
                        // This logic should ideally be more robust for multi-chat support
                        // but for a single active chat, this works
                        if (activeChatId === 'chat-1') {
                            setChats(prevChats => ({
                                ...prevChats,
                                [activeChatId]: serverHistory
                            }));
                        }
                    }
                } catch (error) {
                    console.error("Failed to fetch chat history:", error);
                }
            };
            fetchHistory();
            pollingInterval = setInterval(fetchHistory, 2000);
        }
        return () => clearInterval(pollingInterval);
    }, [sessionToken, activeChatId]);

    const handleChatSubmit = async (message) => {
        setIsLoading(true);
        const userMessage = { sender: 'user', text: message, timestamp: Date.now() };
        setMessages(prev => [...prev, userMessage]);
        
        try {
            const response = await fetch('/chat', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ session_token: sessionToken, message: message }),
            });
            if (!response.ok) throw new Error('Failed to send message.');
        } catch (err) {
            console.error(err);
            setMessages(prev => [...prev, { sender: 'bot', text: 'An error occurred. Please try again.', timestamp: Date.now() }]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleFileUpload = async (file) => {
        if (!file) return;
        setIsLoading(true);
        setMessages(prev => [...prev, { sender: 'user', text: `Uploading file: ${file.name}...`, timestamp: Date.now() }]);

        const formData = new FormData();
        formData.append('file', file);
        formData.append('session_token', sessionToken);

        try {
            const response = await fetch('/upload', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error);
        } catch (err) {
            console.error("Upload failed:", err);
            setMessages(prev => [...prev, { sender: 'bot', text: `Upload failed: ${err.message}`, timestamp: Date.now() }]);
        } finally {
            setIsLoading(false);
        }
    };

    const createNewChat = () => {
        const newChatId = `chat-${Object.keys(chats).length + 1}`;
        setChats(prevChats => ({
            ...prevChats,
            [newChatId]: []
        }));
        setActiveChatId(newChatId);
    };

    const deleteChat = (chatIdToDelete) => {
        const { [chatIdToDelete]: _, ...remainingChats } = chats;
        setChats(remainingChats);
        if (activeChatId === chatIdToDelete) {
            const firstChatId = Object.keys(remainingChats)[0] || 'chat-1';
            setActiveChatId(firstChatId);
        }
    };

    return (
        <div className="bg-slate-700 rounded-xl shadow-lg w-full max-w-7xl h-[90vh] flex">
            {/* Chat History Sidebar */}
            <aside className="w-1/4 bg-slate-800 p-4 flex flex-col rounded-l-xl">
                <h3 className="text-lg font-bold mb-4 text-white">Chat History</h3>
                <div className="flex-1 overflow-y-auto space-y-2">
                    {Object.keys(chats).map(chatId => (
                        <div
                            key={chatId}
                            onClick={() => setActiveChatId(chatId)}
                            className={`p-3 rounded-lg cursor-pointer flex justify-between items-center ${activeChatId === chatId ? 'bg-blue-600' : 'bg-slate-700 hover:bg-slate-600'} text-white transition-colors duration-300`}
                        >
                            <span className="truncate">Chat {chatId.split('-')[1]}</span>
                            {Object.keys(chats).length > 1 && (
                                <button onClick={(e) => { e.stopPropagation(); deleteChat(chatId); }} className="text-sm text-slate-400 hover:text-red-400">
                                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                                        <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z"/>
                                        <path fillRule="evenodd" d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"/>
                                    </svg>
                                </button>
                            )}
                        </div>
                    ))}
                </div>
                <button onClick={createNewChat} className="w-full p-3 mt-4 rounded-lg bg-green-600 hover:bg-green-700 transition-colors duration-300 font-bold text-white flex items-center justify-center space-x-2">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                        <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z"/>
                    </svg>
                    <span>New Chat</span>
                </button>
            </aside>

            {/* Main Chat Area */}
            <div className="flex-1 flex flex-col">
                <header className="bg-slate-600 p-4 rounded-tr-xl flex justify-between items-center shadow">
                    <h2 className="text-xl font-bold text-white">Timesheet Reviewer Bot</h2>
                    <button onClick={onSignOut} className="bg-red-600 text-white px-4 py-2 rounded-lg font-semibold hover:bg-red-700 transition-colors duration-300">
                        Sign Out
                    </button>
                </header>

                <div className="flex-1 overflow-y-auto p-4 space-y-4 flex flex-col">
                    {(chats[activeChatId] || []).map((msg, index) => (
                        <ChatMessage key={index} sender={msg.sender} text={msg.text} />
                    ))}
                </div>

                <div className="bg-slate-600 p-4 rounded-b-xl flex items-center space-x-2">
                    <UploadButton onFileUpload={handleFileUpload} />
                    <ChatInput onChatSubmit={handleChatSubmit} isLoading={isLoading} />
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
