import React, { useState } from 'react';

const ChatInput = ({ onChatSubmit, isLoading }) => {
    const [chatInput, setChatInput] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const message = chatInput.trim();
        if (message) {
            onChatSubmit(message);
            setChatInput('');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="flex-1 flex items-center space-x-2">
            <input
                type="text"
                placeholder="Ask a question about the timesheet..."
                value={chatInput}
                onChange={(e) => setChatInput(e.target.value)}
                className="flex-1 p-3 rounded-lg bg-slate-500 text-white border-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
                disabled={isLoading}
            />
            <button type="submit" className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors duration-300 font-bold" disabled={isLoading}>
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
                </svg>
            </button>
        </form>
    );
};

export default ChatInput;
