// App.jsx
import React, { useState, useEffect } from 'react';
import { initializeApp, getApps } from 'firebase/app';
import { getAuth, onAuthStateChanged, signInWithCustomToken } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';
import Login from './components/Login/Login';
import ChatPage from './components/ChatPage/ChatPage';



const initialAuthToken = null;

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loginError, setLoginError] = useState(''); // NEW: State for login errors

  useEffect(() => {
    if (getApps().length === 0) {
      const app = initializeApp(firebaseConfig);
    }
    const auth = getAuth();
    const db = getFirestore();

    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });

    if (initialAuthToken) {
      signInWithCustomToken(auth, initialAuthToken).catch(console.error);
    }

    return () => unsubscribe();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
        Loading...
      </div>
    );
  }

  if (!user) {
    // UPDATED: Pass the error state and setter to the Login component
    return <Login setLoginError={setLoginError} loginError={loginError} />;
  }

  return <ChatPage user={user} />;
};

export default App;
