import React from 'react';
// import './ChatMessage.css'; // Remove this import if using Tailwind classes exclusively

const ChatMessage = ({ sender, text }) => {
    return (
        // Tailwind classes for styling message bubbles
        <div className={`p-3 rounded-xl max-w-[80%] break-words shadow-md ${sender === 'user' 
                ? 'bg-blue-600 text-white self-end ml-auto' 
                : 'bg-slate-700 text-slate-200 self-start mr-auto'
            }
        `}>
            {text}
        </div>
    );
};

export default ChatMessage;
