import React from 'react';
import './UploadButton.css'; // Import the new CSS

const UploadButton = ({ onFileUpload }) => {
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            onFileUpload(file);
        }
        e.target.value = null; // Reset the input
    };

    return (
        // Use the custom class `upload-label` for styling the button appearance
        <div className="upload-container">
            <label htmlFor="file-upload" className="upload-label">
                {/* We can use a simple SVG icon here for a professional look, if desired */}
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="upload-icon">
                    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                    <polyline points="17 8 12 3 7 8"></polyline>
                    <line x1="12" y1="3" x2="12" y2="15"></line>
                </svg>
                Upload File
            </label>
            <input
                type="file"
                id="file-upload"
                className="upload-input" // This class will hide the default input
                onChange={handleFileChange}
                accept=".csv" // Restrict to CSV files
            />
        </div>
    );
};

export default UploadButton;
