import React, { useState, useEffect, useRef } from 'react';
import './ChatPage.css';
import ChatInput from '../ChatInput/ChatInput'; // Assuming correct path
import UploadButton from '../UploadButton/UploadButton'; // Assuming correct path
import ChatMessage from '../ChatMessage/ChatMessage'; // Assuming correct path

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [uploadStatus, setUploadStatus] = useState('');
    const messagesEndRef = useRef(null);

    // Function to scroll to the bottom of the chat
    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };
    
    // Fetch Chat History Interval
    useEffect(() => {
        const fetchChatHistory = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:5000/chat_history?session_token=${sessionToken}`);
                if (!response.ok) {
                    // Log the error but don't crash the app
                    console.error('Failed to fetch chat history. Status:', response.status);
                    return;
                }
                const data = await response.json();
                // **CORRECTION POINT: Ensure data.history is a safe value.**
                // The backend ensures it's an array, but an extra check for safety is good.
                setMessages(Array.isArray(data.history) ? data.history : []);
            } catch (err) {
                console.error("Failed to fetch chat history:", err);
            }
        };

        fetchChatHistory();
        // Poll for new messages every 3 seconds
        const intervalId = setInterval(fetchChatHistory, 3000);

        return () => clearInterval(intervalId);
    }, [sessionToken]);

    // Scroll to bottom whenever messages update
    useEffect(() => {
        scrollToBottom();
    }, [messages]);


    const handleFileUpload = async (file) => {
        if (!file) return;

        setIsLoading(true);
        setUploadStatus(`Uploading ${file.name}...`);
        
        // Add a temporary user message indicating upload started
        setMessages(prev => [...prev, {
            sender: 'user',
            text: `Uploading timesheet file: ${file.name}...`,
            timestamp: new Date().toISOString()
        }]);

        const formData = new FormData();
        formData.append('file', file);
        formData.append('session_token', sessionToken);
        formData.append('timestamp', new Date().toISOString());

        try {
            const response = await fetch('http://127.0.0.1:5000/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'File upload failed.');
            }

            // The backend automatically adds the bot's success message, 
            // the interval fetch will pick it up.
            setUploadStatus(`Upload of ${file.name} complete!`);

        } catch (err) {
            console.error("Upload Error:", err);
            setUploadStatus(`Error: ${err.message}`);
            // Remove the temporary message or replace it with an error message
            setMessages(prev => [...prev.slice(0, -1), {
                sender: 'bot',
                text: `Sorry, the upload failed: ${err.message}`,
                timestamp: new Date().toISOString()
            }]);
        } finally {
            setIsLoading(false);
            setTimeout(() => setUploadStatus(''), 5000); // Clear status after 5 seconds
        }
    };

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!input.trim() || isLoading) return;

        const userMessage = {
            sender: 'user',
            text: input,
            timestamp: new Date().toISOString()
        };

        // Optimistically update the UI with the user's message
        setMessages(prev => [...prev, userMessage]);
        setInput('');
        setIsLoading(true);
        
        try {
            const response = await fetch('http://127.0.0.1:5000/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query: userMessage.text, session_token: sessionToken }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'Failed to get bot response.');
            }

            const data = await response.json();
            
            // The interval fetch will eventually pick up the new message, 
            // but we can add it immediately for a better user experience.
            if (data.answer) {
                 setMessages(prev => [...prev, {
                    sender: 'bot',
                    text: data.answer,
                    timestamp: new Date().toISOString()
                 }]);
            }
        } catch (err) {
            console.error("Chat Error:", err);
            // Add a bot error message
            setMessages(prev => [...prev, {
                sender: 'bot',
                text: `An error occurred: ${err.message}`,
                timestamp: new Date().toISOString()
            }]);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-card">
                <header className="chat-header">
                    <h1 className="text-xl font-bold">Timesheet Bot ðŸ¤–</h1>
                    <div className="flex items-center space-x-4">
                        <span className="text-sm text-gray-400">Welcome, {user}!</span>
                        <button onClick={onSignOut} className="sign-out-button">
                            Sign Out
                        </button>
                    </div>
                </header>
                
                <div className="flex flex-1 overflow-hidden">
                    {/* Chat History Placeholder - Currently not used in your design */}
                    {/* <ChatHistory history={[]} onSelectChat={() => {}} /> */}

                    <main className="flex-1 flex flex-col p-4 overflow-y-auto">
                        
                        {/* Render Upload Status */}
                        {uploadStatus && (
                            <div className={`p-2 my-2 text-sm text-center rounded-lg ${uploadStatus.startsWith('Error') ? 'bg-red-500' : 'bg-green-500'}`}>
                                {uploadStatus}
                            </div>
                        )}
                        
                        <div className="chat-messages flex-1 flex flex-col space-y-3">
                            {/* **CORRECTION POINT: Safely map over the messages array** */}
                            {Array.isArray(messages) && messages.map((msg, index) => (
                                <ChatMessage 
                                    key={index} 
                                    sender={msg.sender} 
                                    text={msg.text} 
                                />
                            ))}
                            <div ref={messagesEndRef} />
                        </div>
                    </main>
                </div>

                <div className="p-4 border-t border-gray-700 flex items-center space-x-3 bg-gray-800">
                    <UploadButton onFileUpload={handleFileUpload} />
                    <ChatInput onChatSubmit={handleSendMessage} isLoading={isLoading} />
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
