import React, { useState } from 'react';

const Login = ({ onLogin }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isSignUp, setIsSignUp] = useState(false);
    const [error, setError] = useState('');

    const handleAuth = async (e) => {
        e.preventDefault();
        setError('');
        const url = isSignUp ? 'http://127.0.0.1:5000/signup' : 'http://127.0.0.1:5000/login';

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Authentication failed.');
            }

            if (data.session_token) {
                onLogin(username, data.session_token);
            }
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white font-sans p-4">
            <div className="w-full max-w-sm p-8 bg-gray-800 rounded-xl shadow-2xl">
                <h2 className="text-2xl font-bold mb-4 text-center">
                    {isSignUp ? 'Sign Up' : 'Log In'}
                </h2>
                <form onSubmit={handleAuth} className="space-y-4">
                    <input
                        type="text"
                        placeholder="Username"
                        className="w-full p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        className="w-full p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    {error && <div className="text-red-400 text-sm text-center">{error}</div>}
                    <button type="submit" className="w-full p-3 rounded-lg bg-indigo-600 text-white font-medium hover:bg-indigo-700 transition duration-300">
                        {isSignUp ? 'Sign Up' : 'Log In'}
                    </button>
                </form>
                <button
                    onClick={() => setIsSignUp(!isSignUp)}
                    className="mt-4 text-sm text-center text-indigo-400 hover:underline w-full"
                >
                    {isSignUp ? 'Already have an account? Log In' : 'Need an account? Sign Up'}
                </button>
            </div>
        </div>
    );
};

export default Login;
