import React from 'react';
import './ChatSidebar.css'; 
// Import an icon for New Chat and delete
import { MdAdd, MdDelete } from 'react-icons/md';

const ChatSidebar = ({ chatList, currentChatId, onSelectChat, onNewChat, onDeleteChat }) => {
    return (
        <aside className="chat-sidebar">
            <button onClick={onNewChat} className="new-chat-button" title="Start a New Conversation">
                <MdAdd size={24} /> New Chat
            </button>
            <div className="chat-list-container">
                {chatList.length === 0 ? (
                    <p className="no-chats-message">No history yet.</p>
                ) : (
                    chatList.map((chat) => (
                        <div
                            key={chat.id}
                            className={`chat-item ${chat.id === currentChatId ? 'active' : ''}`}
                            onClick={() => onSelectChat(chat.id)}
                            title={chat.title}
                        >
                            <span className="chat-title-text">
                                {chat.title.substring(0, 25)}
                                {chat.title.length > 25 ? '...' : ''}
                            </span>
                            <button 
                                className="delete-chat-button" 
                                onClick={(e) => {
                                    e.stopPropagation(); // Prevent selecting the chat when deleting
                                    onDeleteChat(chat.id);
                                }}
                                title="Delete Chat"
                            >
                                <MdDelete size={18} />
                            </button>
                        </div>
                    ))
                )}
            </div>
        </aside>
    );
};

export default ChatSidebar;
