import React, { useState, useEffect } from 'react';
import ChatMessage from '../ChatMessage/ChatMessage';
import UploadButton from '../UploadButton/UploadButton'; 
import ChatHistory from '../ChatHistory/ChatHistory'; // Import the new component

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [uploadStatus, setUploadStatus] = useState(null); 
    
    // Placeholder data for the ChatHistory component
    const dummyHistory = [
        { id: 1, title: "Timesheet Review Jan", lastUpdated: "2024-01-20" },
        { id: 2, title: "Vacation Policy Qs", lastUpdated: "2024-02-15" },
    ];
    // Placeholder function for when a user selects a chat
    const handleSelectChat = (chatId) => {
        console.log(`Selected chat ID: ${chatId}`);
        // Logic to load chat history will go here later
    };

    const apiUrl = 'http://127.0.0.1:5000';

    // --- Fetch Chat History (Logic remains the same) ---
    useEffect(() => {
        const fetchChatHistory = async () => {
            try {
                const response = await fetch(`${apiUrl}/chat_history`, {
                    headers: { 'Authorization': sessionToken }
                });
                // ... (rest of the history fetch logic)
                if (!response.ok) {
                    throw new Error('Failed to fetch chat history.');
                }
                const data = await response.json();
                setMessages(data.history || []);
            } catch (err) {
                console.error("Failed to fetch chat history:", err);
            }
        };
        fetchChatHistory();
    }, [sessionToken]);

    // --- Send Message Logic (Logic remains the same) ---
    const handleSendMessage = async (userQuery) => {
        if (!userQuery.trim() || isLoading) return;
        // ... (rest of send message logic)
        setIsLoading(true);
        const newMessage = { sender: 'user', text: userQuery, timestamp: new Date().toISOString() };
        setMessages(prev => [...prev, newMessage]);
        setInput('');
        
        try {
            const response = await fetch(`${apiUrl}/chat`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Authorization': sessionToken },
                body: JSON.stringify({ query: userQuery }),
            });

            const data = await response.json();
            if (!response.ok) {
                throw new Error(data.error || 'Failed to get response from bot.');
            }
            const botMessage = { sender: 'bot', text: data.answer, timestamp: new Date().toISOString() };
            setMessages(prev => [...prev, botMessage]);
        } catch (err) {
            console.error("Chat error:", err);
            setMessages(prev => [...prev, { sender: 'bot', text: `Error: ${err.message}`, timestamp: new Date().toISOString() }]);
        } finally {
            setIsLoading(false);
        }
    };
    
    const handleFormSubmit = (e) => {
        e.preventDefault();
        handleSendMessage(input);
    };

    // --- File Upload Logic (Logic remains the same) ---
    const handleFileUpload = async (file) => {
        if (!file || isLoading) return;
        // ... (rest of upload logic)
        setIsLoading(true);
        setUploadStatus(`Uploading ${file.name}...`);
        const formData = new FormData();
        formData.append('file', file);
        
        try {
            const response = await fetch(`${apiUrl}/upload`, {
                method: 'POST',
                headers: { 'Authorization': sessionToken },
                body: formData,
            });

            const data = await response.json();
            if (!response.ok) {
                throw new Error(data.error || data.message || 'File upload failed.');
            }
            const messageText = data.message || 'File processed successfully.';
            setUploadStatus('File uploaded successfully. Chatting is now available.');
            setMessages(prev => [...prev, { sender: 'bot', text: messageText, timestamp: new Date().toISOString() }]);

        } catch (err) {
            console.error("Upload error:", err);
            setUploadStatus(`Error: ${err.message}`);
        } finally {
            setIsLoading(false);
            setTimeout(() => setUploadStatus(null), 5000);
        }
    };

    // --- Component JSX Return (Styled with Three-Column Tailwind Layout) ---
    return (
        // Outer container for the entire application interface
        <div className="flex w-full h-screen bg-slate-900 text-white">
            
            {/* 1. Chat History Sidebar */}
            <ChatHistory history={dummyHistory} onSelectChat={handleSelectChat} />

            {/* 2. Main Chat Area (Chat Card) */}
            <div className="flex flex-col flex-1 bg-slate-800 shadow-2xl overflow-hidden">
                <header className="flex justify-between items-center p-4 bg-slate-700 text-white border-b border-slate-600">
                    <h1 className="text-xl font-bold">Timesheet Bot - User: {user}</h1>
                    <button onClick={onSignOut} className="px-4 py-2 rounded-lg bg-red-600 hover:bg-red-700 transition-colors duration-300 font-medium">Sign Out</button>
                </header>
                
                <div className="flex-1 overflow-y-auto p-4 space-y-4">
                    {messages.map((msg, index) => (
                        <ChatMessage key={index} sender={msg.sender} text={msg.text} />
                    ))}
                    {isLoading && <ChatMessage sender="bot" text="Thinking..." />} 
                </div>

                {/* 3. Input Area */}
                <div className="flex p-4 border-t border-slate-700 bg-slate-800 items-center gap-3">
                    <UploadButton onFileUpload={handleFileUpload} />
                    {uploadStatus && <span className="text-sm text-amber-400">{uploadStatus}</span>}

                    <form onSubmit={handleFormSubmit} className="flex-1 flex items-center space-x-2">
                        <input
                            type="text"
                            placeholder="Ask a question about the timesheet..."
                            className="flex-1 p-3 rounded-lg bg-slate-700 text-white border border-slate-600 focus:ring-2 focus:ring-blue-500 transition-all duration-300"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            disabled={isLoading}
                        />
                        <button type="submit" className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors duration-300 font-bold" disabled={isLoading || !input.trim()}>
                            Send
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
