import React, { useState } from 'react';

const ChatInput = ({ onSend, disabled }) => {
    const [input, setInput] = useState('');

    const handleKeyDown = (e) => {
        if (e.key === 'Enter' && input.trim() && !disabled) {
            onSend(input);
            setInput('');
        }
    };

    return (
        <div className="flex p-4 bg-gray-800 rounded-xl shadow-lg mt-4">
            <input
                type="text"
                className="flex-grow px-4 py-2 mr-2 bg-gray-700 text-white rounded-lg placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="Ask me about the timesheet data..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyDown}
                disabled={disabled}
            />
            <button
                onClick={() => onSend(input)}
                className="px-6 py-2 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200 disabled:bg-gray-600 disabled:cursor-not-allowed"
                disabled={disabled}
            >
                Send
            </button>
        </div>
    );
};

export default ChatInput;
