import React, { useState, useEffect, useRef } from 'react';
import ChatInput from '../ChatInput/ChatInput';
import ChatMessage from '../ChatMessage/ChatMessage';
import UploadButton from '../UploadButton/UploadButton';
import './ChatPage.css';

const API_URL = "http://127.0.0.1:5000/review";

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = async (query) => {
        if (!query) return;

        const newUserMessage = { sender: 'user', text: query };
        setMessages(prevMessages => [...prevMessages, newUserMessage]);
        setLoading(true);

        try {
            const response = await fetch(API_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            });

            const data = await response.json();
            const newBotMessage = { sender: 'bot', text: data.answer };
            setMessages(prevMessages => [...prevMessages, newBotMessage]);
        } catch (error) {
            console.error("Error fetching bot response:", error);
            const errorMessage = { sender: 'bot', text: "Sorry, I couldn't get a response. Please try again." };
            setMessages(prevMessages => [...prevMessages, errorMessage]);
        } finally {
            setLoading(false);
        }
    };

    const handleUpdate = () => {
        const updateMessage = { sender: 'bot', text: "Update successful! File uploaded and data stored successfully." };
        setMessages(prevMessages => [...prevMessages, updateMessage]);
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
            <div style={{ width: '600px', height: '800px' }} className="bg-gray-800 rounded-xl shadow-2xl overflow-hidden flex flex-col">
                <div className="dashboard-container">
                    <div className="main-content flex-1 flex flex-col">
                        <header className="chat-header">
                            <h2>Timesheet Reviewer Bot</h2>
                            <span className="status-online">
                                <span className="status-dot"></span> Status: Online
                            </span>
                        </header>
                        <div className="chat-area flex-1 overflow-y-auto">
                            <div className="chat-messages">
                                {messages.map((msg, index) => (
                                    <ChatMessage key={index} sender={msg.sender} text={msg.text} />
                                ))}
                                {loading && <div className="loading-message">Bot is typing...</div>}
                                <div ref={messagesEndRef} />
                            </div>
                        </div>
                        <div className="flex items-center space-x-2 p-4">
                            <UploadButton handleUpdate={handleUpdate} />
                            <ChatInput onSend={handleSendMessage} disabled={loading} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
