import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import {
    getAuth,
    signInWithCustomToken,
    onAuthStateChanged,
    signInAnonymously
} from 'firebase/auth';
import {
    getFirestore,
    doc,
    setDoc,
    collection,
    query,
    orderBy,
    onSnapshot
} from 'firebase/firestore';

// Component for a styled button
const StyledButton = ({ onClick, children }) => (
    <button
        onClick={onClick}
        className="w-full px-4 py-2 mt-4 text-white font-semibold bg-indigo-600 rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200"
    >
        {children}
    </button>
);

// Component for a login/signup form
const AuthForm = ({ onAuthenticate, isLogin, onToggleAuthMode }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onAuthenticate(username, password, isLogin);
    };

    return (
        <form onSubmit={handleSubmit} className="bg-gray-800 p-8 rounded-xl shadow-2xl w-full max-w-sm">
            <h2 className="text-3xl font-bold text-white text-center mb-6">
                {isLogin ? 'Login' : 'Sign Up'}
            </h2>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-4 py-2 mb-4 bg-gray-700 text-white rounded-lg placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-2 mb-6 bg-gray-700 text-white rounded-lg placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <StyledButton type="submit">{isLogin ? 'Login' : 'Sign Up'}</StyledButton>
            <div className="mt-6 text-center text-gray-400">
                {isLogin ? (
                    <>
                        Don't have an account?{' '}
                        <button type="button" onClick={() => onToggleAuthMode(false)} className="text-indigo-400 hover:underline">
                            Sign Up
                        </button>
                    </>
                ) : (
                    <>
                        Already have an account?{' '}
                        <button type="button" onClick={() => onToggleAuthMode(true)} className="text-indigo-400 hover:underline">
                            Login
                        </button>
                    </>
                )}
            </div>
        </form>
    );
};

// ChatPage component
const ChatPage = ({ messages, setMessages, handleSendMessage, loading }) => {
    return (
        <div className="flex flex-col h-full bg-gray-900 text-white p-6 rounded-2xl shadow-2xl">
            <header className="chat-header text-center py-4 border-b border-gray-700">
                <h2 className="text-3xl font-extrabold text-indigo-400">Timesheet Reviewer Bot</h2>
                <span className="status-online inline-flex items-center text-sm font-medium text-green-400 mt-1">
                    <span className="status-dot w-2 h-2 mr-2 bg-green-500 rounded-full animate-pulse"></span>
                    Status: Online
                </span>
            </header>
            <div className="flex-grow overflow-y-auto my-4 p-4 rounded-xl bg-gray-800 space-y-4">
                {messages.map((msg, index) => (
                    <ChatMessage key={index} sender={msg.sender} text={msg.text} />
                ))}
                {loading && (
                    <div className="flex justify-start items-center">
                        <div className="bg-gray-700 text-gray-300 p-3 rounded-xl rounded-bl-none shadow-md max-w-xs animate-pulse">
                            <span className="block">Bot is typing...</span>
                        </div>
                    </div>
                )}
            </div>
            <ChatInput onSend={handleSendMessage} disabled={loading} />
        </div>
    );
};

// ChatInput component
const ChatInput = ({ onSend, disabled }) => {
    const [input, setInput] = useState('');

    const handleKeyDown = (e) => {
        if (e.key === 'Enter' && input.trim() && !disabled) {
            onSend(input);
            setInput('');
        }
    };

    return (
        <div className="flex p-4 bg-gray-800 rounded-xl shadow-lg mt-4">
            <input
                type="text"
                className="flex-grow px-4 py-2 mr-2 bg-gray-700 text-white rounded-lg placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="Ask me about the timesheet data..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyDown}
                disabled={disabled}
            />
            <button
                onClick={() => onSend(input)}
                className="px-6 py-2 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200 disabled:bg-gray-600 disabled:cursor-not-allowed"
                disabled={disabled}
            >
                Send
            </button>
        </div>
    );
};

// ChatMessage component
const ChatMessage = ({ sender, text }) => {
    const isUser = sender === 'user';
    const messageClass = isUser ? 'justify-end' : 'justify-start';
    const bubbleClass = isUser ? 'bg-indigo-600 text-white rounded-br-none' : 'bg-gray-700 text-gray-100 rounded-bl-none';
    const messageAlign = isUser ? 'text-right' : 'text-left';

    return (
        <div className={`flex ${messageClass}`}>
            <div className={`flex flex-col ${messageAlign}`}>
                <div className={`p-4 rounded-xl shadow-md max-w-xl ${bubbleClass}`}>
                    {text}
                </div>
            </div>
        </div>
    );
};

// Main App component
const App = () => {
    const [user, setUser] = useState(null);
    const [isLogin, setIsLogin] = useState(true);
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);
    const [userId, setUserId] = useState(null);

    // Correctly accessing the global variables to avoid ReferenceError
    const appId = typeof window.__app_id !== 'undefined' ? window.__app_id : 'default-app-id';
    const firebaseConfig = typeof window.__firebase_config !== 'undefined' ? JSON.parse(window.__firebase_config) : {};
    const initialAuthToken = typeof window.__initial_auth_token !== 'undefined' ? window.__initial_auth_token : null;

    useEffect(() => {
        if (firebaseConfig && Object.keys(firebaseConfig).length > 0) {
            try {
                const app = initializeApp(firebaseConfig);
                const authInstance = getAuth(app);
                const dbInstance = getFirestore(app);
                setAuth(authInstance);
                setDb(dbInstance);

                const unsubscribe = onAuthStateChanged(authInstance, async (authUser) => {
                    if (authUser) {
                        setUser(authUser);
                        setUserId(authUser.uid);
                    } else {
                        setUser(null);
                        setUserId(null);
                        setMessages([]);
                    }
                });

                if (initialAuthToken) {
                    signInWithCustomToken(authInstance, initialAuthToken).catch((error) => {
                        console.error("Error signing in with custom token:", error);
                        signInAnonymously(authInstance).catch(anonError => {
                            console.error("Error signing in anonymously:", anonError);
                        });
                    });
                } else {
                    signInAnonymously(authInstance).catch(error => {
                        console.error("Error signing in anonymously:", error);
                    });
                }

                return () => unsubscribe();
            } catch (error) {
                console.error("Firebase initialization failed:", error);
            }
        }
    }, [firebaseConfig, initialAuthToken]);

    useEffect(() => {
        if (db && userId) {
            const chatHistoryQuery = query(
                collection(db, `/artifacts/${appId}/users/${userId}/chat_history`),
                orderBy('timestamp')
            );

            const unsubscribe = onSnapshot(chatHistoryQuery, (snapshot) => {
                const history = snapshot.docs.map(doc => doc.data());
                setMessages(history);
            }, (error) => {
                console.error("Error fetching real-time chat history:", error);
            });

            return () => unsubscribe();
        }
    }, [db, userId, appId]);

    const handleAuthenticate = async (username, password, isLogin) => {
        // Here you would typically call a backend endpoint to handle login/signup
        // For this example, we'll just simulate success.
        console.log(`Authenticating user: ${username}, isLogin: ${isLogin}`);
        // Simulate a successful login
        setUser({ uid: 'simulated-user-id' });
        setUserId('simulated-user-id');
    };

    const handleSendMessage = async (query) => {
        if (!query || !db || !userId) return;

        const newUserMessage = { sender: 'user', text: query, timestamp: Date.now() };
        setLoading(true);

        try {
            // Save user message to Firestore
            await setDoc(doc(collection(db, `/artifacts/${appId}/users/${userId}/chat_history`)), newUserMessage);
            
            // Call the backend API
            const API_URL = "http://127.0.0.1:5000/review";
            const response = await fetch(API_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ query }),
            });

            const data = await response.json();
            const newBotMessage = { sender: 'bot', text: data.answer, timestamp: Date.now() };
            
            // Save bot message to Firestore
            await setDoc(doc(collection(db, `/artifacts/${appId}/users/${userId}/chat_history`)), newBotMessage);
        } catch (error) {
            console.error("Error fetching bot response or saving to DB:", error);
            const errorMessage = { sender: 'bot', text: "Sorry, I couldn't get a response. Please try again.", timestamp: Date.now() };
            // Save error message to Firestore
            await setDoc(doc(collection(db, `/artifacts/${appId}/users/${userId}/chat_history`)), errorMessage);
        } finally {
            setLoading(false);
        }
    };

    const handleToggleAuthMode = (mode) => {
      setIsLogin(mode);
    };

    if (!user) {
        return (
            <div className="flex justify-center items-center h-screen bg-gray-900">
                <AuthForm onAuthenticate={handleAuthenticate} isLogin={isLogin} onToggleAuthMode={handleToggleAuthMode} />
            </div>
        );
    }

    return (
        <div className="flex flex-col h-screen bg-gray-900">
            <div className="flex-grow p-4">
                <ChatPage
                    messages={messages}
                    setMessages={setMessages}
                    handleSendMessage={handleSendMessage}
                    loading={loading}
                />
            </div>
        </div>
    );
};

export default App;
