import React, { useState } from 'react';
import './Login.css';

const API_BASE_URL = 'http://127.0.0.1:5000'; // Define base URL

const Login = ({ onLogin }) => {
    // State for login/signup credentials
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // State for additional sign-up fields
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    
    // UI State
    const [isSignUp, setIsSignUp] = useState(false);
    const [isResetFlow, setIsResetFlow] = useState(false); // NEW STATE for Reset Flow
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    // Helper to determine CSS class for messages
    const getMessageClass = () => {
        if (error) return 'login-error';
        if (successMessage) return 'login-success';
        return '';
    };

    const handleAuth = async (e) => {
        e.preventDefault();
        setError('');
        setSuccessMessage('');
        
        // Skip API call if we are in the password reset flow
        if (isResetFlow) return; 

        const url = isSignUp ? `${API_BASE_URL}/signup` : `${API_BASE_URL}/login`;

        const body = isSignUp 
            ? JSON.stringify({ username, password, full_name: fullName, email }) 
            : JSON.stringify({ username, password });

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: body,
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Authentication failed.');
            }

            // Successful login/signup
            if (data.token) {
                onLogin(data.token, username);
            } else {
                setSuccessMessage(data.message || 'Account created successfully! Please log in.');
                setIsSignUp(false); // Move to login after successful sign-up
            }
        } catch (err) {
            setError(err.message);
        }
    };
    
    // NEW HANDLER: Submits the email for a password reset request
    const handlePasswordReset = async (e) => {
        e.preventDefault();
        setError('');
        setSuccessMessage('');

        if (!email.trim()) {
            setError("Please enter your email address to reset your password.");
            return;
        }

        // --- Mocked API Call for Prototype ---
        // Replace this entire block with your real backend API call later
        // Example: await fetch(`${API_BASE_URL}/forgot_password`, { method: 'POST', body: JSON.stringify({ email }) });
        
        setTimeout(() => {
            setSuccessMessage("If an account exists for that email, a password reset link has been sent.");
            setIsResetFlow(false); // Automatically return to login form
            setEmail('');
        }, 1500);
        
        // --- End Mocked API Call ---
    };

    const handleToggleAuth = () => {
        setIsSignUp(prev => !prev);
        setIsResetFlow(false); // Exit reset flow when toggling auth mode
        setError('');
        setSuccessMessage('');
        setUsername('');
        setPassword('');
        setEmail('');
        setFullName('');
    };
    
    const handleOpenResetFlow = () => {
        setIsResetFlow(true);
        setError('');
        setSuccessMessage('');
        // Clear username/password fields as only email is needed
        setUsername('');
        setPassword('');
    };

    // Determine the action handler based on the flow
    const formHandler = isResetFlow ? handlePasswordReset : handleAuth;
    
    // --- Render ---
    return (
        <div className="login-container">
            <div className="login-card">
                <h1 className="login-title">
                    {isResetFlow ? 'Reset Password' : (isSignUp ? 'Sign Up' : 'Log In')}
                </h1>

                <form onSubmit={formHandler} className="login-form">
                    
                    {/* Show only email input for reset flow */}
                    {(isSignUp || isResetFlow) && (
                         <input
                            type="email"
                            placeholder={isResetFlow ? "Enter your email address" : "Email"}
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="login-input"
                            required
                         />
                    )}

                    {/* Show Full Name only for sign up */}
                    {isSignUp && (
                         <input
                            type="text"
                            placeholder="Full Name"
                            value={fullName}
                            onChange={(e) => setFullName(e.target.value)}
                            className="login-input"
                            required
                         />
                    )}
                    
                    {/* Show Username and Password only for Auth flow */}
                    {!isResetFlow && (
                        <>
                            <input
                                type="text"
                                placeholder="Username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                className="login-input"
                                required
                            />
                            <input
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="login-input"
                                required
                            />
                        </>
                    )}
                    
                    {/* --- Forgot Password Button (Visible only on Login view) --- */}
                    {!isSignUp && !isResetFlow && (
                         <button type="button" onClick={handleOpenResetFlow} className="forgot-password-button">
                            Forgot Password?
                         </button>
                    )}
                    
                    {/* Error/Success Message */}
                    {(error || successMessage) && (
                        <div className={getMessageClass()}>
                            {error || successMessage}
                        </div>
                    )}
                    
                    <button 
                        type="submit" 
                        className="login-button"
                        disabled={
                            isResetFlow ? !email.trim() : 
                            !username.trim() || !password.trim() || (isSignUp && (!fullName.trim() || !email.trim()))
                        }
                    >
                        {isResetFlow ? 'Send Reset Link' : (isSignUp ? 'Sign Up' : 'Log In')}
                    </button>
                    
                </form>
                
                <button
                    onClick={handleToggleAuth}
                    className="toggle-auth-button"
                >
                    {isSignUp ? 'Already have an account? Log In' : 'Need an account? Sign Up'}
                </button>
                
                {/* Back button for Reset Flow */}
                {isResetFlow && (
                    <button onClick={() => setIsResetFlow(false)} className="toggle-auth-button">
                        Back to Login
                    </button>
                )}
            </div>
        </div>
    );
};

export default Login;
