// App.jsx
import React, { useState, useEffect } from 'react';
import { initializeApp, getApps, getApp } from 'firebase/app';
import { getAuth, onAuthStateChanged, signInWithCustomToken } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';
import Login from './components/Login/Login'; // Corrected import path
import ChatPage from './components/ChatPage/ChatPage'; // Corrected import path



const initialAuthToken = null;

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loginError, setLoginError] = useState('');
  const [auth, setAuth] = useState(null); // NEW: State to hold the auth instance

  useEffect(() => {
    // Check if Firebase has already been initialized
    if (getApps().length === 0) {
      initializeApp(firebaseConfig);
    }
    const currentAuth = getAuth();
    setAuth(currentAuth); // Save the auth instance to state
    const db = getFirestore();

    const unsubscribe = onAuthStateChanged(currentAuth, async (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });

    if (initialAuthToken) {
      signInWithCustomToken(currentAuth, initialAuthToken).catch(console.error);
    }

    return () => unsubscribe();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
        Loading...
      </div>
    );
  }

  if (!user) {
    // Pass auth and the error state to the Login component
    return <Login auth={auth} setLoginError={setLoginError} loginError={loginError} />;
  }

  return <ChatPage user={user} />;
};

export default App;
