import React, { useRef, useEffect } from 'react';
import ChatMessage from '../ChatMessage/ChatMessage.jsx';
import ChatInput from '../ChatInput/ChatInput.jsx';
import { getAuth, signOut } from 'firebase/auth';

const ChatPage = ({ messages, handleSendMessage, loading, handleUpdate }) => {
    const messagesEndRef = useRef(null);
    const auth = getAuth();

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(scrollToBottom, [messages]);
    
    const handleSignOut = () => {
        signOut(auth).catch(error => {
            console.error("Error signing out:", error);
        });
    };

    return (
        <div className="flex flex-col h-screen bg-gray-900 p-6 sm:p-12 md:p-20">
            <div className="flex flex-col flex-grow bg-gray-800 text-white rounded-2xl shadow-2xl p-6">
                <header className="chat-header flex justify-between items-center py-4 border-b border-gray-700 mb-4">
                    <h2 className="text-3xl font-extrabold text-indigo-400">Timesheet Reviewer Bot</h2>
                    <span className="status-online inline-flex items-center text-sm font-medium text-green-400">
                        <span className="status-dot w-2 h-2 mr-2 bg-green-500 rounded-full animate-pulse"></span>
                        Status: Online
                    </span>
                    <button 
                        onClick={handleSignOut}
                        className="px-4 py-2 text-white font-semibold bg-red-600 rounded-lg shadow-md hover:bg-red-700 transition-colors duration-200"
                    >
                        Sign Out
                    </button>
                </header>
                <div className="flex-grow overflow-y-auto pr-2" style={{ scrollbarWidth: 'thin', scrollbarColor: '#4b5563 #1f2937' }}>
                    <div className="space-y-4">
                        {messages.map((msg, index) => (
                            <ChatMessage key={index} sender={msg.sender} text={msg.text} />
                        ))}
                        {loading && (
                            <div className="flex justify-start items-center">
                                <div className="bg-gray-700 text-gray-300 p-3 rounded-xl rounded-bl-none shadow-md max-w-xs animate-pulse">
                                    <span className="block">Bot is typing...</span>
                                </div>
                            </div>
                        )}
                        <div ref={messagesEndRef} />
                    </div>
                </div>
                <ChatInput onSend={handleSendMessage} disabled={loading} onUpdate={handleUpdate} />
            </div>
        </div>
    );
};

export default ChatPage;
