import React from 'react';

const ChatInput = ({ onChatSubmit, isLoading, input, setInput }) => {
    // The component now relies on props for state management

    const handleSubmit = (e) => {
        e.preventDefault();
        const message = input.trim();
        if (message) {
            // Pass the input value up to the ChatPage handler
            onChatSubmit(e); // Pass the event to ChatPage to handle input state there
            // Note: ChatPage is now responsible for clearing the input via setInput
        }
    };
    
    // Note: The form now surrounds the input and send button, and takes up the remaining space (flex-1)
    return (
        <form onSubmit={handleSubmit} className="flex-1 flex items-center bg-gray-600 rounded-full border border-gray-500 overflow-hidden">
            <input
                type="text"
                placeholder="Ask a question about the timesheet..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                className="flex-1 p-3 px-5 text-lg bg-transparent text-white border-none focus:outline-none placeholder-gray-400"
                disabled={isLoading}
            />
            <button 
                type="submit" 
                className="bg-blue-600 text-white p-3 h-full hover:bg-blue-700 transition-colors duration-300 font-bold disabled:bg-gray-500 disabled:cursor-not-allowed" 
                disabled={isLoading || !input.trim()}
            >
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                    {/* Send Icon */}
                    <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
                </svg>
            </button>
        </form>
    );
};

export default ChatInput;
