import React from 'react';

const UploadButton = ({ onFileUpload }) => {
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        onFileUpload(file);
        e.target.value = null; // Reset the input so the same file can be uploaded again
    };

    return (
        <div>
            <label htmlFor="file-upload" className="cursor-pointer bg-slate-500 hover:bg-slate-400 transition-colors duration-300 px-4 py-3 rounded-lg font-bold text-white">
                Upload
            </label>
            <input
                type="file"
                id="file-upload"
                className="hidden"
                onChange={handleFileChange}
            />
        </div>
    );
};

export default UploadButton;
