import React, { useState, useEffect } from 'react';
import './Login.css';

const API_BASE_URL = 'http://127.0.0.1:5000'; 

const Login = ({ onLogin }) => {
    // State for login/signup credentials
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [newPassword, setNewPassword] = useState(''); 
    
    // State for additional sign-up fields
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    const [phoneNumber, setPhoneNumber] = useState(''); // NEW STATE
    const [country, setCountry] = useState('');         // NEW STATE
    
    // UI State
    const [isSignUp, setIsSignUp] = useState(false);
    // resetStage: 0=Login/SignUp, 1=Verify Username, 2=Change Password
    const [resetStage, setResetStage] = useState(0); 
    const [error, setError] = useState('');

    // Clears fields when component mounts or authentication mode changes
    useEffect(() => {
        setUsername('');
        setPassword('');
        setNewPassword(''); 
        setEmail('');
        setFullName('');
        setPhoneNumber(''); 
        setCountry('');     
        setError('');
    }, [isSignUp, resetStage]); 

    // Helper function to determine CSS class for messages
    const getErrorClass = () => {
        if (!error) return '';
        return error.includes('successful') || error.includes('Success') ? 'login-success' : 'login-error';
    }

    // --- Primary Authentication Handler (Used for /login and /signup) ---
    const handleAuth = async (e) => {
        e.preventDefault();
        setError('');
        
        const url = isSignUp ? `${API_BASE_URL}/signup` : `${API_BASE_URL}/login`;

        // UPDATED BODY TO INCLUDE ALL SIGNUP FIELDS
        const body = isSignUp 
            ? JSON.stringify({ 
                username, 
                password, 
                full_name: fullName, 
                email,
                phone_number: phoneNumber, // ADDED
                country: country           // ADDED
            }) 
            : JSON.stringify({ username, password });

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: body,
            });

            const data = await response.json();

            if (!response.ok) {
                setPassword('');
                throw new Error(data.error || 'Authentication failed.');
            }

            const authToken = data.session_token;

            if (authToken) {
                // Successful LOGIN
                onLogin(authToken, username);
            } else {
                // Successful SIGNUP: Show message and switch to Log In view
                setError(data.message || 'Account created successfully! Please log in.');
                setIsSignUp(false);
            }
        } catch (err) {
            setError(err.message);
        }
    };
    
    // --- Forgot Password Handlers ---
    const handleOpenResetFlow = () => {
        setError('');
        setResetStage(1); 
        setUsername(''); 
        setPassword('');
    };

    const handleUsernameCheck = async (e) => {
        e.preventDefault();
        setError('');

        if (!username.trim()) {
            setError("Please enter your username.");
            return;
        }

        try {
            const response = await fetch(`${API_BASE_URL}/verify_username`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Username verification failed.');
            }

            setResetStage(2); 
            setError(data.message || `Hello ${username}! Please enter your new password.`);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleChangePassword = async (e) => {
        e.preventDefault();
        setError('');

        if (!newPassword.trim() || newPassword.length < 6) {
            setError("New password must be at least 6 characters.");
            return;
        }

        try {
            const response = await fetch(`${API_BASE_URL}/reset_password`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, new_password: newPassword }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Password reset failed.');
            }

            setError(data.message || 'Password successfully reset. Please log in.');
            setResetStage(0); 
            setNewPassword('');
        } catch (err) {
            setError(err.message);
        }
    };

    const handleToggleAuth = () => {
        setIsSignUp(prev => !prev);
        setResetStage(0); 
    };
    
    const handleBackToLogin = () => {
        setResetStage(0);
    };
    
    let formHandler = handleAuth;
    if (resetStage === 1) {
        formHandler = handleUsernameCheck;
    } else if (resetStage === 2) {
        formHandler = handleChangePassword;
    }
    
    // --- Render ---
    return (
        <div className="login-container">
            <div className="login-card">
                <h2 className="login-title">
                    {resetStage === 1 ? 'Verify Username' : 
                     resetStage === 2 ? 'Set New Password' : 
                     (isSignUp ? 'Sign Up' : 'Log In')}
                </h2>

                <form onSubmit={formHandler} className="login-form" autoComplete="off">
                    
                    {/* --- Sign Up Specific Fields --- */}
                    {isSignUp && resetStage === 0 && (
                         <>
                            <input
                                type="text"
                                placeholder="Full Name"
                                className="login-input"
                                value={fullName}
                                onChange={(e) => setFullName(e.target.value)}
                                required
                                autoComplete="name" 
                            />
                            <input
                                type="email"
                                placeholder="Email"
                                className="login-input"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                                autoComplete="email"
                            />
                            {/* NEW FIELD: Phone Number */}
                            <input
                                type="tel"
                                placeholder="Phone Number"
                                className="login-input"
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                                required
                                autoComplete="tel" 
                            />
                            {/* NEW FIELD: Country */}
                            <input
                                type="text"
                                placeholder="Country"
                                className="login-input"
                                value={country}
                                onChange={(e) => setCountry(e.target.value)}
                                required
                                autoComplete="country-name" 
                            />
                        </>
                    )}
                    
                    {/* --- Username Input (Common Field) --- */}
                    {(resetStage > 0 || !isSignUp) ? (
                         <input
                            type="text"
                            placeholder="Username"
                            className="login-input"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                            disabled={resetStage === 2} 
                            autoComplete="username" 
                        />
                    ) : (
                        <input
                            type="text"
                            placeholder="Username"
                            className="login-input"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                            autoComplete="username" 
                        />
                    )}
                    
                    {/* --- Password Input (Login/Sign Up) --- */}
                    {resetStage === 0 && (
                        <input
                            type="password"
                            placeholder="Password"
                            className="login-input"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            autoComplete={isSignUp ? "new-password" : "current-password"} 
                        />
                    )}

                    {/* --- New Password Input (Reset Stage 2) --- */}
                    {resetStage === 2 && (
                         <input
                            type="password"
                            placeholder="Enter New Password"
                            className="login-input"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                            required
                            autoComplete="new-password"
                        />
                    )}
                    
                    {/* --- Forgot Password Button --- */}
                    {resetStage === 0 && !isSignUp && (
                         <button type="button" onClick={handleOpenResetFlow} className="forgot-password-button">
                            Forgot Password?
                         </button>
                    )}
                    
                    {/* Error/Success Message */}
                    {error && <div className={getErrorClass()}>{error}</div>}
                    
                    <button 
                        type="submit" 
                        className="login-button"
                        // UPDATED DISABLED LOGIC TO INCLUDE NEW FIELDS
                        disabled={
                             (resetStage === 1 && !username.trim()) ||
                             (resetStage === 2 && !newPassword.trim()) ||
                             (resetStage === 0 && (
                                !username.trim() || 
                                !password.trim() || 
                                (isSignUp && (!fullName.trim() || !email.trim() || !phoneNumber.trim() || !country.trim()))
                             ))
                        }
                    >
                        {resetStage === 1 ? 'Verify' : (resetStage === 2 ? 'Change Password' : (isSignUp ? 'Sign Up' : 'Log In'))}
                    </button>
                    
                </form>
                
                {/* --- Toggle/Back Buttons --- */}
                
                {/* Toggle Button (Log In/Sign Up) */}
                {resetStage === 0 && (
                    <button
                        onClick={handleToggleAuth}
                        className="toggle-auth-button"
                    >
                        {isSignUp ? 'Already have an account? Log In' : 'Need an account? Sign Up'}
                    </button>
                )}
                
                {/* Back button for Reset Flow */}
                {resetStage > 0 && (
                    <button onClick={handleBackToLogin} className="toggle-auth-button">
                        ← Back to Login
                    </button>
                )}
            </div>
        </div>
    );
};

export default Login;
