import React, { useState, useEffect } from 'react';
import './Login.css';

const API_BASE_URL = 'http://127.0.0.1:5000';

const Login = ({ onLogin }) => {
    // State for credentials
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // State for additional sign-up fields
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    
    // UI State
    const [isSignUp, setIsSignUp] = useState(false);
    // REMOVED: [successMessage, setSuccessMessage] is removed.
    const [error, setError] = useState('');

    // Clears fields when component mounts or authentication mode changes
    useEffect(() => {
        setUsername('');
        setPassword('');
        setFullName('');
        setEmail('');
        setError('');
        // Removed setSuccessMessage('') cleanup
    }, [isSignUp]); 

    // Helper to determine CSS class for messages: now only checks the content of 'error'
    const getMessageClass = () => {
        if (!error) return '';
        // If the message contains success-related keywords, use the success class
        if (error.includes('successful') || error.includes('Log In now')) {
            return 'login-success';
        }
        return 'login-error';
    }

    const handleAuth = async (e) => {
        e.preventDefault();
        setError('');

        const url = isSignUp ? `${API_BASE_URL}/signup` : `${API_BASE_URL}/login`;

        const body = isSignUp 
            ? JSON.stringify({ username, password, full_name: fullName, email }) 
            : JSON.stringify({ username, password });

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: body,
            });

            const data = await response.json();

            if (!response.ok) {
                setPassword(''); 
                throw new Error(data.error || 'Authentication failed.');
            }

            if (isSignUp) {
                // Sign-up success message set to trigger 'login-success' class
                setError('Sign-up successful! Please log in now.');
                setIsSignUp(false);
            } else if (data.session_token) {
                // Successful login
                onLogin(username, data.session_token);
            }
        } catch (err) {
            setError(err.message);
        }
    };

    const handleForgotPassword = () => {
        setError('Password reset is not enabled for this prototype. Please ensure you are using the correct credentials.');
        
        setTimeout(() => {
            setError('');
        }, 5000); 
    };

    const handleToggleAuth = () => {
        setIsSignUp(!isSignUp);
    }

    // --- Render ---
    return (
        <div className="login-container">
            <div className="login-card">
                <h2 className="login-title">
                    {isSignUp ? 'Sign Up' : 'Log In'}
                </h2>
                <form onSubmit={handleAuth} className="login-form" autoComplete="off">
                    
                    {/* --- Sign Up Specific Fields --- */}
                    {isSignUp && (
                        <>
                            <input
                                type="text"
                                placeholder="Full Name"
                                className="login-input"
                                value={fullName}
                                onChange={(e) => setFullName(e.target.value)}
                                required
                                autoComplete="false"
                            />
                            <input
                                type="email"
                                placeholder="Email"
                                className="login-input"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                                autoComplete="false"
                            />
                        </>
                    )}

                    {/* --- Common Fields --- */}
                    <input
                        type="text"
                        placeholder="Username"
                        className="login-input"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        autoComplete="false"
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        className="login-input"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        autoComplete="new-password"
                    />
                    
                    {/* --- Forgot Password Button --- */}
                    {!isSignUp && (
                         <button type="button" onClick={handleForgotPassword} className="forgot-password-button">
                            Forgot Password?
                         </button>
                    )}
                    
                    {/* Error/Success Message */}
                    {error && <div className={getMessageClass()}>{error}</div>}
                    
                    <button 
                        type="submit" 
                        className="login-button"
                        disabled={!username.trim() || !password.trim() || (isSignUp && (!fullName.trim() || !email.trim()))}
                    >
                        {isSignUp ? 'Sign Up' : 'Log In'}
                    </button>
                    
                </form>
                
                <button
                    onClick={handleToggleAuth}
                    className="toggle-auth-button"
                >
                    {isSignUp ? 'Already have an account? Log In' : 'Need an account? Sign Up'}
                </button>
            </div>
        </div>
    );
};

export default Login;
