import React, { useState } from 'react';
import './Login.css';

const Login = ({ onLogin }) => {
    // State for login/signup credentials
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // State for password reset
    const [newPassword, setNewPassword] = useState('');
    const [confirmNewPassword, setConfirmNewPassword] = useState('');
    // State for additional sign-up fields
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    
    // UI State
    const [isSignUp, setIsSignUp] = useState(false);
    const [isForgotPassword, setIsForgotPassword] = useState(false);
    const [error, setError] = useState('');

    // Utility function to clear all form fields
    const clearForm = () => {
        setUsername('');
        setPassword('');
        setNewPassword('');
        setConfirmNewPassword('');
        setFullName('');
        setEmail('');
        setError('');
    }

    const handleAuth = async (e) => {
        e.preventDefault();
        setError('');
        const apiUrl = 'http://127.0.0.1:5000';
        const url = isSignUp ? `${apiUrl}/signup` : `${apiUrl}/login`;

        const body = isSignUp 
            ? JSON.stringify({ username, password, full_name: fullName, email }) 
            : JSON.stringify({ username, password });

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: body,
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Authentication failed.');
            }

            if (isSignUp) {
                setError('Sign-up successful! Please log in now.');
                clearForm();
                setIsSignUp(false);
                
            } else if (data.session_token) {
                onLogin(username, data.session_token);
            }
        } catch (err) {
            setError(err.message);
        }
    };

    // NEW FUNCTION: Handle Password Reset Submission
    const handlePasswordReset = async (e) => {
        e.preventDefault();
        setError('');

        if (newPassword !== confirmNewPassword) {
            setError("New password and confirmation do not match.");
            return;
        }

        if (newPassword.length < 8) {
             setError("New password must be at least 8 characters long.");
             return;
        }

        try {
            const response = await fetch('http://127.0.0.1:5000/reset_password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, new_password: newPassword }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Failed to reset password. Check the username.');
            }

            setError('Password successfully reset! Please log in with your new password.');
            clearForm();
            setIsForgotPassword(false);
            
        } catch (err) {
            setError(err.message);
        }
    }

    const handleToggleAuth = () => {
        clearForm();
        setIsSignUp(!isSignUp);
        setIsForgotPassword(false);
    }

    const handleForgotPasswordClick = () => {
        clearForm();
        setIsForgotPassword(true);
        setIsSignUp(false);
    }
    
    const handleBackToLogin = () => {
        clearForm();
        setIsForgotPassword(false);
    }

    // Function to handle the custom success class for styling
    const getErrorClass = () => {
        if (!error) return '';
        return error.includes('successful') || error.includes('reset') ? 'login-success' : 'login-error';
    }

    // --- Conditional Rendering ---
    const renderForm = () => {
        if (isForgotPassword) {
            return (
                <form onSubmit={handlePasswordReset} className="login-form" autoComplete="off">
                    <input
                        type="text"
                        placeholder="Username"
                        className="login-input"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        autoComplete="username" 
                    />
                    <input
                        type="password"
                        placeholder="New Password (min 8 chars)"
                        className="login-input"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        required
                        autoComplete="new-password" 
                    />
                    <input
                        type="password"
                        placeholder="Confirm New Password"
                        className="login-input"
                        value={confirmNewPassword}
                        onChange={(e) => setConfirmNewPassword(e.target.value)}
                        required
                        autoComplete="new-password" 
                    />
                    {error && <div className={getErrorClass()}>{error}</div>}
                    <button type="submit" className="login-button">
                        Reset Password
                    </button>
                    <button 
                        type="button" 
                        onClick={handleBackToLogin} 
                        className="toggle-auth-button"
                    >
                        Back to Login
                    </button>
                </form>
            );
        }

        // --- Log In / Sign Up Form ---
        return (
            <form onSubmit={handleAuth} className="login-form" autoComplete="off">
                
                {/* Sign Up Specific Fields */}
                {isSignUp && (
                    <>
                        <input
                            type="text"
                            placeholder="Full Name"
                            className="login-input"
                            value={fullName}
                            onChange={(e) => setFullName(e.target.value)}
                            required
                            autoComplete="name"
                        />
                        <input
                            type="email"
                            placeholder="Email"
                            className="login-input"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            autoComplete="email"
                        />
                    </>
                )}

                {/* Common Fields */}
                <input
                    type="text"
                    placeholder="Username"
                    className="login-input"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    autoComplete="username"
                />
                <input
                    type="password"
                    placeholder="Password"
                    className="login-input"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    autoComplete="current-password"
                />
                
                {/* Forgot Password Button */}
                {!isSignUp && (
                    <button type="button" onClick={handleForgotPasswordClick} className="forgot-password-button">
                        Forgot Password?
                    </button>
                )}
                
                {error && <div className={getErrorClass()}>{error}</div>}
                
                <button 
                    type="submit" 
                    className="login-button"
                    disabled={!username.trim() || !password.trim() || (isSignUp && (!fullName.trim() || !email.trim()))}
                >
                    {isSignUp ? 'Sign Up' : 'Log In'}
                </button>
            </form>
        );
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2 className="login-title">
                    {isForgotPassword ? 'Reset Password' : isSignUp ? 'Sign Up' : 'Log In'}
                </h2>
                {renderForm()}
                
                {/* Toggle button only visible on Login/Signup view */}
                {!isForgotPassword && (
                    <button
                        onClick={handleToggleAuth}
                        className="toggle-auth-button"
                    >
                        {isSignUp ? 'Already have an account? Log In' : 'Need an account? Sign Up'}
                    </button>
                )}
            </div>
        </div>
    );
};

export default Login;
