import React, { useState, useEffect, useRef } from 'react';
import './ChatPage.css'; 
import ChatMessage from '../ChatMessage/ChatMessage';
import UploadButton from '../UploadButton/UploadButton'; 

const API_BASE_URL = 'http://127.0.0.1:5000';

const ChatPage = ({ user, sessionToken, onSignOut }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const chatEndRef = useRef(null);

    // Scroll to the bottom of the chat area when messages change
    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    // Fetch chat history on component mount or whenever the token is set/changes
    useEffect(() => {
        if (sessionToken) {
            fetchChatHistory();
        }
    }, [sessionToken]);

    const fetchChatHistory = async () => {
        // ... (fetchChatHistory function body is omitted for brevity but should be included)
    };

    const handleSendMessage = async (e) => {
        // ... (handleSendMessage function body is omitted for brevity but should be included)
    };

    const handleFileUpload = async (file) => {
        // ... (handleFileUpload function body is omitted for brevity but should be included)
    };

    return (
        <div className="chat-container">
            <div className="chat-card">
                <header className="chat-header">
                    <h1 className="chat-title">Timesheet Chatbot</h1>
                    <button onClick={onSignOut} className="signout-button">
                        Sign Out
                    </button>
                </header>
                
                <main className="chat-messages">
                    {messages.map((msg, index) => (
                        <ChatMessage 
                            key={index} 
                            sender={msg.sender} 
                            text={msg.text} 
                        />
                    ))}
                    <div ref={chatEndRef} />
                    {isLoading && <ChatMessage sender="bot" text="Thinking..." />}
                </main>
                
                <footer className="chat-footer">
                     {error && <div className="chat-error">{error}</div>}
                    <div className="chat-input-form"> {/* CRITICAL ALIGNMENT CONTAINER */}
                        <UploadButton onFileUpload={handleFileUpload} disabled={isLoading} /> 
                        
                        <form onSubmit={handleSendMessage} className="chat-input-form-inner">
                            <input
                                type="text"
                                placeholder={isLoading ? "Processing, please wait..." : "Ask a question about the timesheet..."}
                                className="chat-input"
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                disabled={isLoading}
                            />
                            <button type="submit" className="send-button" disabled={!input.trim() || isLoading}>
                                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor" style={{width:'20px', height:'20px'}}>
                                    <path strokeLinecap="round" strokeLinejoin="round" d="M6 12L3.269 3.126A59.768 59.768 0 0121.485 12 59.77 59.77 0 013.27 20.876L5.999 12zm0 0h7.5" />
                                </svg>
                            </button>
                        </form>
                    </div>
                </footer>
            </div>
        </div>
    );
};

export default ChatPage;
